mapper.select:
SELECT /* com.at.apcss.am.cmns.mapper.CmnsItemMapper.selectApcCmnsItemList */ AIC.APC_CD AS APC_CD , AIC.ITEM_CD AS ITEM_CD , AIC.ITEM_NM AS ITEM_NM , AIC.DEL_YN AS DEL_YN , AIC.RAW_MTR_RDCD_RT AS RAW_MTR_RDCD_RT , AIC.SORT_RDCD_RT AS SORT_RDCD_RT , AIC.PCKG_RDCD_RT AS PCKG_RDCD_RT , AIC.EXTRNL_LNKG_CD AS EXTRNL_LNKG_CD , AIC.HSTRY_TRC_MNG_NO AS HSTRY_TRC_MNG_NO , AIC.GAP_CERT_NO AS GAP_CERT_NO , (SELECT COUNT(*) FROM TB_APC_VRTY_CRTR X WHERE X.APC_CD = AIC.APC_CD AND X.ITEM_CD = AIC.ITEM_CD AND X.DEL_YN = 'N') AS VRTR_CNT , (SELECT COUNT(*) FROM TB_APC_SPCFCT_CRTR X WHERE X.APC_CD = AIC.APC_CD AND X.ITEM_CD = AIC.ITEM_CD AND X.DEL_YN = 'N') AS SPCFCT_CNT , (SELECT COUNT(*) FROM TB_APC_STD_GRD X WHERE APC_CD = AIC.APC_CD AND X.ITEM_CD = AIC.ITEM_CD AND X.DEL_YN = 'N') AS GRD_CNT , AIC.INVNTR_INQ_AU_CD AS INVNTR_INQ_AU_CD , CASE WHEN INSTR(AIC.INVNTR_INQ_AU_CD, 'P') > 0 THEN 'Y' ELSE 'N' END AS AU_PRDCR_YN , CASE WHEN INSTR(AIC.INVNTR_INQ_AU_CD, 'V') > 0 THEN 'Y' ELSE 'N' END AS AU_VRTY_YN , AIC.USE_YN FROM TB_APC_ITEM_CRTR AIC WHERE AIC.DEL_YN = 'N' AND AIC.APC_CD = #{apcCd}
AND AIC.EXTRNL_LNKG_CD = #{extrnlLnkgCd}
ORDER BY AIC.ITEM_CD

mapper.insert:

    		INSERT /* com.at.apcss.am.cmns.mapper.CmnsItemMapper.insertCmnsItem */
    			INTO TB_APC_ITEM_CRTR
	    		(
	    			  APC_CD
	    		  	, ITEM_CD
	    		  	, ITEM_NM
	    		  	, SYS_FRST_INPT_DT
	    		  	, SYS_FRST_INPT_USER_ID
	    		  	, SYS_FRST_INPT_PRGRM_ID
	    		  	, SYS_LAST_CHG_DT
	    		  	, SYS_LAST_CHG_USER_ID
	    		  	, SYS_LAST_CHG_PRGRM_ID
	    		)
   			VALUES
	   			(
	   				  #{apcCd}
	    		  	, #{itemCd}
	    		  	, #{itemNm}
	    		  	, SYSDATE
	    		  	, #{sysFrstInptUserId}
	    		  	, #{sysFrstInptPrgrmId}
	    		  	, SYSDATE
	    		  	, #{sysLastChgUserId}
	    		  	, #{sysLastChgPrgrmId}
	   			)

mapper.update:

		UPDATE
			TB_APC_ITEM_CRTR
		   SET
		   	  SYS_LAST_CHG_DT		= SYSDATE
    		, SYS_LAST_CHG_USER_ID	= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}
			, RAW_MTR_RDCD_RT		= #{rawMtrRdcdRt}
    		, SORT_RDCD_RT			= #{sortRdcdRt}
    		, PCKG_RDCD_RT			= #{pckgRdcdRt}
		    , EXTRNL_LNKG_CD		= #{extrnlLnkgCd}
		    , HSTRY_TRC_MNG_NO		= #{hstryTrcMngNo}
		    , GAP_CERT_NO			= #{gapCertNo}
		    , INVNTR_INQ_AU_CD		= #{invntrInqAuCd}
		    , USE_YN				= #{useYn}
		 WHERE APC_CD 		= #{apcCd}
		   AND ITEM_CD		= #{itemCd}

mapper.delete:

DELETE /* com.at.apcss.am.cmns.mapper.CnptMapper.deleteCmnsItem */
	FROM TB_APC_ITEM_CRTR
 WHERE APC_CD		= #{apcCd}
   AND ITEM_CD 		= #{itemCd}

mapper.update:

    	   UPDATE TB_APC_ITEM_CRTR
		      SET SYS_LAST_CHG_DT = SYSDATE
		   		, SYS_LAST_CHG_USER_ID=#{sysLastChgUserId}
		   		, SYS_LAST_CHG_PRGRM_ID=#{sysLastChgPrgrmId}
		   		, EXTRNL_LNKG_CD=#{extrnlLnkgCd}
		   WHERE APC_CD=#{apcCd}
		     AND ITEM_CD=#{itemCd}

mapper.select:
SELECT APC_CD AS APC_CD ,ITEM_CD AS ITEM_CD ,ITEM_DTL_CD AS ITEM_DTL_CD ,ITEM_DTL_NM AS ITEM_DTL_NM ,INDCT_SEQ AS INDCT_SEQ ,USE_YN AS USE_YN ,GRD_MNG_CD AS GRD_MNG_CD FROM TB_APC_ITEM_CRTR_DTL WHERE APC_CD = #{apcCd} AND ITEM_CD = #{itemCd} AND DEL_YN = 'N'
AND USE_YN = #{useYn}
ORDER BY INDCT_SEQ, ITEM_DTL_CD

mapper.update:
MERGE INTO TB_APC_ITEM_CRTR_DTL ICD USING (
SELECT #{item.apcCd} AS APC_CD, #{item.itemCd} AS ITEM_CD, #{item.itemDtlCd} AS ITEM_DTL_CD, #{item.itemDtlNm} AS ITEM_DTL_NM, #{item.indctSeq} AS INDCT_SEQ, #{item.useYn} AS USE_YN, #{item.delYn} AS DEL_YN, #{item.sysFrstInptDt} AS SYS_FRST_INPT_DT, #{item.sysFrstInptUserId} AS SYS_FRST_INPT_USER_ID, #{item.sysFrstInptPrgrmId} AS SYS_FRST_INPT_PRGRM_ID, #{item.sysLastChgDt} AS SYS_LAST_CHG_DT, #{item.sysLastChgUserId} AS SYS_LAST_CHG_USER_ID, #{item.sysLastChgPrgrmId} AS SYS_LAST_CHG_PRGRM_ID, #{item.grdMngCd} AS GRD_MNG_CD FROM DUAL
) SRC ON ( ICD.APC_CD = SRC.APC_CD AND ICD.ITEM_CD = SRC.ITEM_CD AND ICD.ITEM_DTL_CD = SRC.ITEM_DTL_CD ) WHEN MATCHED THEN UPDATE SET ICD.ITEM_DTL_NM = SRC.ITEM_DTL_NM, ICD.INDCT_SEQ = SRC.INDCT_SEQ, ICD.USE_YN = SRC.USE_YN, ICD.DEL_YN = SRC.DEL_YN, ICD.SYS_LAST_CHG_DT = SYSDATE, ICD.SYS_LAST_CHG_USER_ID = SRC.SYS_LAST_CHG_USER_ID, ICD.SYS_LAST_CHG_PRGRM_ID = SRC.SYS_LAST_CHG_PRGRM_ID, ICD.GRD_MNG_CD = SRC.GRD_MNG_CD WHEN NOT MATCHED THEN INSERT ( APC_CD, ITEM_CD, ITEM_DTL_CD, ITEM_DTL_NM, INDCT_SEQ, USE_YN, DEL_YN, SYS_FRST_INPT_DT, SYS_FRST_INPT_USER_ID, SYS_FRST_INPT_PRGRM_ID, SYS_LAST_CHG_DT, SYS_LAST_CHG_USER_ID, SYS_LAST_CHG_PRGRM_ID, GRD_MNG_CD ) VALUES ( SRC.APC_CD, SRC.ITEM_CD, SRC.ITEM_DTL_CD, SRC.ITEM_DTL_NM, SRC.INDCT_SEQ, SRC.USE_YN, SRC.DEL_YN, SYSDATE, SRC.SYS_FRST_INPT_USER_ID, SRC.SYS_FRST_INPT_PRGRM_ID, SYSDATE, SRC.SYS_LAST_CHG_USER_ID, SRC.SYS_LAST_CHG_PRGRM_ID, SRC.GRD_MNG_CD );

MERGE INTO TB_APC_SEED_CRTR SC USING (
SELECT #{item.apcCd} AS APC_CD, #{item.seedCd} AS SEED_CD, #{item.seedNm} AS SEED_NM, #{item.indctNm} AS INDCT_NM, #{item.itemCd} AS ITEM_CD, #{item.vrtyCd} AS VRTY_CD, #{item.indctSeq} AS INDCT_SEQ, #{item.delYn} AS DEL_YN, #{item.sysFrstInptDt} AS SYS_FRST_INPT_DT, #{item.sysFrstInptUserId} AS SYS_FRST_INPT_USER_ID, #{item.sysFrstInptPrgrmId} AS SYS_FRST_INPT_PRGRM_ID, #{item.sysLastChgDt} AS SYS_LAST_CHG_DT, #{item.sysLastChgUserId} AS SYS_LAST_CHG_USER_ID, #{item.sysLastChgPrgrmId} AS SYS_LAST_CHG_PRGRM_ID FROM DUAL
) SRC ON ( SC.APC_CD = SRC.APC_CD AND SC.SEED_CD = SRC.SEED_CD ) WHEN MATCHED THEN UPDATE SET SC.SEED_NM = SRC.SEED_NM, SC.INDCT_NM = SRC.INDCT_NM, SC.ITEM_CD = SRC.ITEM_CD, SC.VRTY_CD = SRC.VRTY_CD, SC.INDCT_SEQ = SRC.INDCT_SEQ, SC.DEL_YN = SRC.DEL_YN, SC.SYS_LAST_CHG_DT = SYSDATE, SC.SYS_LAST_CHG_USER_ID = SRC.SYS_LAST_CHG_USER_ID, SC.SYS_LAST_CHG_PRGRM_ID = SRC.SYS_LAST_CHG_PRGRM_ID WHEN NOT MATCHED THEN INSERT ( APC_CD, SEED_CD, SEED_NM, INDCT_NM, ITEM_CD, VRTY_CD, INDCT_SEQ, DEL_YN, SYS_FRST_INPT_DT, SYS_FRST_INPT_USER_ID, SYS_FRST_INPT_PRGRM_ID, SYS_LAST_CHG_DT, SYS_LAST_CHG_USER_ID, SYS_LAST_CHG_PRGRM_ID ) VALUES ( SRC.APC_CD, SRC.SEED_CD, SRC.SEED_NM, SRC.INDCT_NM, SRC.ITEM_CD, SRC.VRTY_CD, SRC.INDCT_SEQ, SRC.DEL_YN, SYSDATE, SRC.SYS_FRST_INPT_USER_ID, SRC.SYS_FRST_INPT_PRGRM_ID, SYSDATE, SRC.SYS_LAST_CHG_USER_ID, SRC.SYS_LAST_CHG_PRGRM_ID );

UPDATE
	TB_APC_ITEM_CRTR_DTL
SET
	DEL_YN = 'Y'
WHERE
	APC_CD = #{apcCd}
AND
	ITEM_CD = #{itemCd}
AND
	ITEM_DTL_CD = #{itemDtlCd}
