mapper.select:
SELECT /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.selectCmnsSpcfctList */ GC.ITEM_CD , GC.SPCFCT_CD , GC.SPCFCT_NM , GC.UNIT_CD , GC.PCKG_STTS_CD , GC.BX_GDS_QNTT , GC.WGHT , GC.DEL_YN FROM TB_SPCFCT_CRTR GC WHERE GC.ITEM_CD = #{itemCd}
AND GC.SPCFCT_CD LIKE #{spcfctCd} || '%'
AND GC.SPCFCT_NM LIKE '%' || #{spcfctNm} || '%'
AND GC.DEL_YN = #{delYn}
AND NOT EXISTS ( SELECT 1 FROM TB_APC_SPCFCT_CRTR X WHERE X.APC_CD = #{apcCd} AND X.SPCFCT_CD = GC.SPCFCT_CD AND X.ITEM_CD = GC.ITEM_CD )

mapper.insert:

    	INSERT /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.insertCmnsSpcfct */
    		INTO TB_SPCFCT_CRTR
			(
				  ITEM_CD
				, SPCFCT_CD
	    		, SPCFCT_NM
	    		, UNIT_CD
				, PCKG_STTS_CD
				, BX_GDS_QNTT
				, WGHT
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				  #{itemCd}
				, #{spcfctCd}
	    		, #{spcfctNm}
	    		, #{unitCd}
				, #{pckgSttsCd}
				, #{bxGdsQntt}
				, #{wght}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.updateCmnsSpcfct */
			TB_SPCFCT_CRTR
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, SPCFCT_NM					= #{spcfctNm}
			, UNIT_CD					= #{unitCd}
			, PCKG_STTS_CD				= #{pckgSttsCd}
			, BX_GDS_QNTT				= #{bxGdsQntt}
			, WGHT  					= #{wght}
    		, DEL_YN					= #{delYn}
		 WHERE ITEM_CD = #{itemCd}
		   AND SPCFCT_CD = #{spcfctCd}

mapper.delete:

DELETE /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.deleteCmnsSpcfct */
  FROM TB_SPCFCT_CRTR
 WHERE ITEM_CD = #{itemCd}
   AND SPCFCT_CD = #{spcfctCd}

mapper.select:

    	SELECT /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.selectApcSpcfct */
			  AGC.ITEM_CD
			, AGC.SPCFCT_CD
			, AGC.SPCFCT_NM
		    , AGC.DEL_YN
		    , AGC.APC_CD
		  FROM TB_APC_SPCFCT_CRTR AGC
		 WHERE AGC.ITEM_CD = #{itemCd}
		   AND AGC.SPCFCT_CD = #{spcfctCd}
		   AND AGC.APC_CD = #{apcCd}

SELECT /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.selectApcSpcfctList */ AGC.ITEM_CD AS ITEM_CD , (SELECT FN_GET_ITEM_NM(AGC.ITEM_CD, AGC.APC_CD) FROM DUAL) AS ITEM_NM , AGC.APC_CD AS APC_CD , AGC.SPCFCT_CD AS SPCFCT_CD , AGC.SPCFCT_NM AS SPCFCT_NM , AGC.UNIT_CD AS UNIT_CD , AGC.PCKG_STTS_CD AS PCKG_STTS_CD , AGC.BX_GDS_QNTT AS BX_GDS_QNTT , AGC.WGHT AS WGHT , AGC.DEL_YN AS DEL_YN , AGC.SN AS SN , AGC.EXTRNL_LNKG_CD AS EXTRNL_LNKG_CD , CONCAT(AGC.ITEM_CD, AGC.SPCFCT_CD) AS ITEM_SPCFCT_CD FROM TB_APC_SPCFCT_CRTR AGC WHERE AGC.APC_CD = #{apcCd}
AND AGC.ITEM_CD = #{itemCd}
AND AGC.SPCFCT_CD LIKE #{spcfctCd} || '%'
AND AGC.SPCFCT_NM LIKE '%' || #{spcfctNm} || '%'
AND AGC.DEL_YN = #{delYn}
ORDER BY SN

mapper.insert:

    	INSERT /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.insertApcSpcfct */
    		INTO TB_APC_SPCFCT_CRTR
    		(
    			  APC_CD
    			, ITEM_CD
    			, SPCFCT_CD
				, SPCFCT_NM
				, UNIT_CD
				, PCKG_STTS_CD
				, BX_GDS_QNTT
				, WGHT
				, SYS_FRST_INPT_DT
				, SYS_FRST_INPT_USER_ID
				, SYS_FRST_INPT_PRGRM_ID
				, SYS_LAST_CHG_DT
				, SYS_LAST_CHG_USER_ID
				, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				  #{apcCd}
   				, #{itemCd}
   				, FN_GET_ID_APC_SPCFCT(#{apcCd}, #{itemCd})
				, #{spcfctNm}
				, #{unitCd}
				, #{pckgSttsCd}
				, #{bxGdsQntt}
				, #{wght}
				, SYSDATE
				, #{sysFrstInptUserId}
				, #{sysFrstInptPrgrmId}
				, SYSDATE
				, #{sysLastChgUserId}
				, #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.updateApcSpcfct */
			TB_APC_SPCFCT_CRTR
		   SET
			  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, SPCFCT_NM					= #{spcfctNm}
			, UNIT_CD					= #{unitCd}
			, PCKG_STTS_CD				= #{pckgSttsCd}
			, BX_GDS_QNTT				= #{bxGdsQntt}
			, WGHT  					= #{wght}
    		, DEL_YN					= #{delYn}
		 WHERE ITEM_CD = #{itemCd}
		   AND SPCFCT_CD = #{spcfctCd}
		   AND APC_CD = #{apcCd}

mapper.delete:

DELETE /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.deleteApcSpcfct */
  FROM TB_APC_SPCFCT_CRTR
 WHERE ITEM_CD = #{itemCd}
   AND SPCFCT_CD = #{spcfctCd}
   AND APC_CD = #{apcCd}


DELETE /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.deleteApcSpcfct */
  FROM TB_APC_SPCFCT_CRTR
 WHERE ITEM_CD = #{itemCd}
   AND APC_CD = #{apcCd}

mapper.select:

    		SELECT X.* /* com.at.apcss.am.cmns.mapper.CmnsSpcfctMapper.spcfctDelible */
			  FROM (
			         SELECT
			                '선별실적' AS DELIBLE
			               , (SELECT COUNT(*)
			                    FROM TB_SORT_PRFMNC X
			                       , TB_SORT_INVNTR Y
			                   WHERE X.APC_CD = Y.APC_CD
			                     AND X.SORTNO = Y.SORTNO
			                     AND X.APC_CD = #{apcCd}
			                     AND Y.ITEM_CD = #{itemCd}
			                     AND Y.SPCFCT_CD = #{spcfctCd}
			                     AND X.DEL_YN = 'N'
			                     AND Y.DEL_YN = 'N'
			                  )  AS CNT
			           FROM DUAL
			         UNION ALL
			         SELECT
			                '선별재고' AS DELIBLE
			                , (SELECT COUNT(*)
			                    FROM TB_SORT_INVNTR X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.ITEM_CD = #{itemCd}
			                     AND X.SPCFCT_CD = #{spcfctCd}
			                     AND X.DEL_YN = 'N'
			                  )  AS CNT
			           FROM DUAL
			          UNION ALL
			          SELECT
			                '포장지시' AS DELIBLE
			               , (SELECT COUNT(*)
			                    FROM TB_PCKG_CMND X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.ITEM_CD = #{itemCd}
			                     AND X.SPCFCT_CD = #{spcfctCd}
			                     AND X.DEL_YN = 'N'
			                  )  AS CNT
			            FROM DUAL
			          UNION ALL
			          SELECT
			                 '포장실적' AS DELIBLE
			                 , (SELECT COUNT(*)
			                     FROM TB_PCKG_PRFMNC X
			                        , TB_GDS_INVNTR Y
			                    WHERE X.APC_CD = Y.APC_CD
			                      AND X.PCKGNO = Y.PCKGNO
			                      AND X.APC_CD = #{apcCd}
			                      AND Y.ITEM_CD = #{itemCd}
			                      AND Y.SPCFCT_CD = #{spcfctCd}
			                      AND X.DEL_YN = 'N'
			                      AND Y.DEL_YN = 'N'
			                  )  AS CNT
			            FROM DUAL
			          UNION ALL
			          SELECT
			                 '상품재고' AS DELIBLE
			                , (SELECT COUNT(*)
			                     FROM TB_GDS_INVNTR X
			                    WHERE X.APC_CD = #{apcCd}
			                      AND X.ITEM_CD = #{itemCd}
			                      AND X.SPCFCT_CD = #{spcfctCd}
			                      AND X.DEL_YN = 'N'
			                 )  AS CNT
			             FROM DUAL
			           UNION ALL
			           SELECT
						      '상품' AS DELIBLE
						     , (SELECT COUNT(*)
						          FROM TB_SPMT_PCKG_UNIT X
						         WHERE X.APC_CD = #{apcCd}
						           AND X.ITEM_CD = #{itemCd}
						           AND X.SPCFCT_CD = #{spcfctCd}
						           AND X.DEL_YN = 'N'
						      )  AS CNT
						 FROM DUAL
			           UNION ALL
			           SELECT
			                  '출하지시' AS DELIBLE
			                 , (SELECT COUNT(*)
			                      FROM TB_SPMT_CMND X
			                     WHERE X.APC_CD = #{apcCd}
			                       AND X.ITEM_CD = #{itemCd}
			                       AND X.SPCFCT_CD = #{spcfctCd}
			                       AND X.DEL_YN = 'N'
			                  )  AS CNT
			             FROM DUAL
			           UNION ALL
			           SELECT
			                   '출하실적' AS DELIBLE
			                  , (SELECT COUNT(*)
			                       FROM TB_SPMT_PRFMNC_DTL X
			                      WHERE X.APC_CD = #{apcCd}
			                        AND X.ITEM_CD = #{itemCd}
			                        AND X.SPCFCT_CD = #{spcfctCd}
			                        AND X.DEL_YN = 'N'
			                   )  AS CNT
			             FROM DUAL
			           UNION ALL
			           SELECT
			                  '매출실적' AS DELIBLE
			                 , (SELECT COUNT(*)
			                      FROM TB_SLS_PRFMNC X
			                     WHERE X.APC_CD = #{apcCd}
			                       AND X.ITEM_CD = #{itemCd}
			                       AND X.SPCFCT_CD = #{spcfctCd}
			                       AND X.DEL_YN = 'N'
			                  )  AS CNT
			             FROM DUAL
			         ) X
			 WHERE CNT > 0
