mapper.select:
SELECT /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.selectCmnsVrtyList */ VC.ITEM_CD , VC.VRTY_CD , VC.VRTY_NM , VC.DEL_YN FROM TB_VRTY_CRTR VC WHERE VC.ITEM_CD = #{itemCd}
AND VC.VRTY_CD LIKE #{vrtyCd} || '%'
AND VC.VRTY_NM LIKE '%' || #{vrtyNm} || '%'
AND VC.DEL_YN = #{delYn}
AND NOT EXISTS ( SELECT 1 FROM TB_APC_VRTY_CRTR X WHERE X.APC_CD = #{apcCd} AND X.VRTY_CD = VC.VRTY_CD AND X.ITEM_CD = VC.ITEM_CD )

mapper.insert:

    	INSERT /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.insertCmnsVrty */
