			(
				  ITEM_CD
				, VRTY_CD
	    		, VRTY_NM
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				  #{itemCd}
				, #{vrtyCd}
	    		, #{vrtyNm}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.updateCmnsVrty */
			TB_VRTY_CRTR
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, VRTY_NM					= #{vrtyNm}
    		, DEL_YN					= #{delYn}
		 WHERE ITEM_CD = #{itemCd}
		   AND VRTY_CD = #{vrtyCd}

mapper.delete:

DELETE /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.deleteCmnsVrty */
  FROM TB_VRTY_CRTR
 WHERE ITEM_CD = #{itemCd}
   AND VRTY_CD = #{vrtyCd}

mapper.select:
SELECT /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.selectApcVrtyList */ AVC.APC_CD AS APC_CD , AVC.ITEM_CD AS ITEM_CD , (SELECT FN_GET_ITEM_NM(AVC.ITEM_CD, AVC.APC_CD) FROM DUAL) AS ITEM_NM , AVC.VRTY_CD AS VRTY_CD , AVC.VRTY_NM AS VRTY_NM , CONCAT(AVC.ITEM_CD, AVC.VRTY_CD) AS ITEM_VRTY_CD , AVC.UNIT_WGHT AS UNIT_WGHT , AVC.WGHT_RKNG_SE_CD AS WGHT_RKNG_SE_CD , AVC.NH_APC_CD AS NH_APC_CD , AVC.NH_VRTY_NM AS NH_VRTY_NM , AVC.SN AS SN , AVC.DEL_YN AS DEL_YN , AVC.EXTRNL_LNKG_CD AS EXTRNL_LNKG_CD FROM TB_APC_VRTY_CRTR AVC WHERE AVC.APC_CD = #{apcCd}
AND AVC.ITEM_CD = #{itemCd}
AND AVC.VRTY_CD LIKE #{vrtyCd} || '%'
AND AVC.VRTY_NM LIKE '%' || #{vrtyNm} || '%'
AND AVC.DEL_YN = #{delYn}
AND AVC.EXTRNL_LNKG_CD = #{extrnlLnkgCd}
ORDER BY ITEM_CD, SN, VRTY_CD

mapper.insert:

	    	INSERT /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.insertApcVrty */
	    		INTO TB_APC_VRTY_CRTR
