mapper.select:

    	SELECT X.* /* com.at.apcss.am.cmns.mapper.CmnsVrtyMapper.vrtyDelible */
		  FROM (
		         SELECT
		                '입고실적' AS DELIBLE
		               , (SELECT COUNT(*)
		                    FROM TB_RAW_MTR_WRHS_PRFMNC X
		                       , TB_RAW_MTR_INVNTR Y
		                   WHERE X.APC_CD = Y.APC_CD
		                     AND X.WRHSNO = Y.WRHSNO
		                     AND X.APC_CD = #{apcCd}
		                     AND Y.ITEM_CD = #{itemCd}
		                     AND Y.VRTY_CD = #{vrtyCd}
		                     AND X.DEL_YN = 'N'
		                     AND Y.DEL_YN = 'N'
		                  )  AS CNT
		           FROM DUAL
		         UNION ALL
		         SELECT
		                '원물재고' AS DELIBLE
		                , (SELECT COUNT(*)
		                    FROM TB_RAW_MTR_INVNTR X
		                   WHERE X.APC_CD = #{apcCd}
		                     AND X.ITEM_CD = #{itemCd}
		                     AND X.VRTY_CD = #{vrtyCd}
		                     AND X.DEL_YN = 'N'
		                  )  AS CNT
		           FROM DUAL
		         UNION ALL
		         SELECT
		                '선별실적' AS DELIBLE
		               , (SELECT COUNT(*)
		                    FROM TB_SORT_PRFMNC X
		                       , TB_SORT_INVNTR Y
		                   WHERE X.APC_CD = Y.APC_CD
		                     AND X.SORTNO = Y.SORTNO
		                     AND X.APC_CD = #{apcCd}
		                     AND Y.ITEM_CD = #{itemCd}
		                     AND Y.VRTY_CD = #{vrtyCd}
		                     AND X.DEL_YN = 'N'
		                     AND Y.DEL_YN = 'N'
		                  )  AS CNT
		           FROM DUAL
		         UNION ALL
		         SELECT
		                '선별재고' AS DELIBLE
		                , (SELECT COUNT(*)
		                    FROM TB_SORT_INVNTR X
		                   WHERE X.APC_CD = #{apcCd}
		                     AND X.ITEM_CD = #{itemCd}
		                     AND X.VRTY_CD = #{vrtyCd}
		                     AND X.DEL_YN = 'N'
		                  )  AS CNT
		           FROM DUAL
		          UNION ALL
		          SELECT
		                '포장지시' AS DELIBLE
		               , (SELECT COUNT(*)
		                    FROM TB_PCKG_CMND X
		                   WHERE X.APC_CD = #{apcCd}
		                     AND X.ITEM_CD = #{itemCd}
		                     AND X.VRTY_CD = #{vrtyCd}
		                     AND X.DEL_YN = 'N'
		                  )  AS CNT
		            FROM DUAL
		          UNION ALL
		          SELECT
		                 '포장실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                     FROM TB_PCKG_PRFMNC X
		                        , TB_GDS_INVNTR Y
		                    WHERE X.APC_CD = Y.APC_CD
		                      AND X.PCKGNO = Y.PCKGNO
		                      AND X.APC_CD = #{apcCd}
		                      AND Y.ITEM_CD = #{itemCd}
		                      AND Y.VRTY_CD = #{vrtyCd}
		                      AND X.DEL_YN = 'N'
		                      AND Y.DEL_YN = 'N'
		                  )  AS CNT
		            FROM DUAL
		          UNION ALL
		          SELECT
		                 '상품재고' AS DELIBLE
		                , (SELECT COUNT(*)
		                     FROM TB_GDS_INVNTR X
		                    WHERE X.APC_CD = #{apcCd}
		                      AND X.ITEM_CD = #{itemCd}
		                      AND X.VRTY_CD = #{vrtyCd}
		                      AND X.DEL_YN = 'N'
		                 )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '상품' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SPMT_PCKG_UNIT X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.VRTY_CD = #{vrtyCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '출하지시' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SPMT_CMND X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.VRTY_CD = #{vrtyCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                   '출하실적' AS DELIBLE
		                  , (SELECT COUNT(*)
		                       FROM TB_SPMT_PRFMNC_DTL X
		                      WHERE X.APC_CD = #{apcCd}
		                        AND X.ITEM_CD = #{itemCd}
		                        AND X.VRTY_CD = #{vrtyCd}
		                        AND X.DEL_YN = 'N'
		                   )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '매출실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SLS_PRFMNC X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.VRTY_CD = #{vrtyCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '정산단가' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_CLCLN_UNTPRC X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.VRTY_CD = #{vrtyCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		            FROM DUAL
		           UNION ALL
		           SELECT
		                  '정산실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_CLCLN_PRFMNC X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.VRTY_CD = #{vrtyCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		            FROM DUAL
		         ) X
		 WHERE CNT > 0
