mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT CT.APC_CD AS APC_CD , CT.CNPT_CD AS CNPT_CD , CT.CNPT_NM AS CNPT_NM , CT.BRNO AS BRNO , CT.CNPT_TYPE AS CNPT_TYPE , (SELECT FN_GET_COM_CD_VL_NM('CNPT_TYPE', CT.CNPT_TYPE) FROM DUAL) AS CNPT_TYPE_NM , CT.PIC_NM AS PIC_NM , CT.TELNO AS TELNO , CT.EML AS EML , CT.CLS AS CLS , CT.BZSTAT AS BZSTAT , CT.WHLSL_MRKT_CORP_CD AS WHLSL_MRKT_CORP_CD , CT.RMRK AS RMRK , CT.DEL_YN AS DEL_YN , CT.EXTRNL_LNKG_CD AS EXTRNL_LNKG_CD FROM TB_CNPT_INFO CT WHERE CT.DEL_YN = 'N' AND CT.APC_CD = #{apcCd}
AND CT.CNPT_NM LIKE '%' || #{cnptNm} || '%'
ORDER BY CNPT_NM
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

