mapper.update:

		UPDATE TB_CNPT_INFO
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , CNPT_NM					= #{cnptNm}
    		 , BRNO						= #{brno}
    		 , CNPT_TYPE				= #{cnptType}
    		 , PIC_NM					= #{picNm}
    		 , TELNO					= #{telno}
    		 , EML						= #{eml}
    		 , RMRK						= #{rmrk}
    		 , CLS 						= #{cls}
		     , BZSTAT					= #{bzstat}
		     , WHLSL_MRKT_CORP_CD		= #{whlslMrktCorpCd}
		 WHERE APC_CD 					= #{apcCd}
		   AND CNPT_CD 					= #{cnptCd}


UPDATE TB_CNPT_INFO /* com.at.apcss.am.cmns.mapper.CnptMapper.deleteCnpt */
   SET DEL_YN 		= 'Y'
 WHERE APC_CD		= #{apcCd}
   AND CNPT_CD 		= #{cnptCd}


		UPDATE /* com.at.apcss.am.cmns.mapper.CnptMapper.updateLgszMrkt */
			TB_LGSZ_MRKT_MNG
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
    		 , USER_ID 	= #{userId}


    		 , PSWD 	= #{pswd}


    		 , USE_YN 	= #{useYn}
		 WHERE APC_CD 					= #{apcCd}
  		 AND LGSZ_MRKT_CD 				= #{lgszMrktCd}

mapper.insert:

