mapper.select:
SELECT /* com.at.apcss.am.cmns.mapper.CnptSpmtPckgUnitMapper.selectCnptSpmtPckgUnitList */ CSPU.APC_CD AS APC_CD , CSPU.CNPT_CD AS CNPT_CD , CSPU.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , CSPU.RMRK AS RMRK , CSPU.USE_YN AS USE_YN , CSPU.DEL_YN AS DEL_YN , SPU.ITEM_CD AS ITEM_CD , SPU.VRTY_CD AS VRTY_CD , SPU.SPCFCT_CD AS SPCFCT_CD FROM TB_CNPT_SPMT_PCKG_UNIT CSPU , TB_SPMT_PCKG_UNIT SPU WHERE CSPU.APC_CD = SPU.APC_CD AND CSPU.DEL_YN = 'N' AND SPU.DEL_YN = 'N' AND CSPU.APC_CD = #{apcCd} AND CSPU.CNPT_CD = #{cnptCd}
AND SPU.ITEM_CD = #{itemCd}
AND SPU.VRTY_CD = #{vrtyCd}
AND CSPU.SPMT_PCKG_UNIT_CD= #{spmtPckgUnitCd}
AND SPU.SPCFCT_CD = #{spcfctCd}
ORDER BY SPU.ITEM_CD, CSPU.SPMT_PCKG_UNIT_CD

mapper.update:

UPDATE TB_CNPT_SPMT_PCKG_UNIT /* com.at.apcss.am.cmns.mapper.CnptSpmtPckgUnitMapper.deleteCnptSpmtPckgUnit */
   SET DEL_YN 				= 'Y'
 WHERE APC_CD				= #{apcCd}
   AND SPMT_PCKG_UNIT_CD 	= #{spmtPckgUnitCd}
   AND CNPT_CD	= #{cnptCd}

MERGE INTO TB_CNPT_SPMT_PCKG_UNIT CSPU USING (
SELECT #{item.apcCd} AS APC_CD , #{item.cnptCd} AS CNPT_CD , #{item.spmtPckgUnitCd} AS SPMT_PCKG_UNIT_CD , #{item.rmrk} AS RMRK , #{item.useYn} AS USE_YN , #{item.delYn} AS DEL_YN , #{item.sysFrstInptDt} AS SYS_FRST_INPT_DT , #{item.sysFrstInptUserId} AS SYS_FRST_INPT_USER_ID , #{item.sysFrstInptPrgrmId} AS SYS_FRST_INPT_PRGRM_ID , #{item.sysLastChgDt} AS SYS_LAST_CHG_DT , #{item.sysLastChgUserId} AS SYS_LAST_CHG_USER_ID , #{item.sysLastChgPrgrmId} AS SYS_LAST_CHG_PRGRM_ID FROM DUAL
) source ON ( CSPU.APC_CD = source.APC_CD AND CSPU.SPMT_PCKG_UNIT_CD = source.SPMT_PCKG_UNIT_CD AND CSPU.CNPT_CD = source.CNPT_CD ) WHEN MATCHED THEN UPDATE SET CSPU.RMRK = source.RMRK , CSPU.USE_YN = source.USE_YN , CSPU.DEL_YN = source.DEL_YN , CSPU.SYS_LAST_CHG_DT = SYSDATE , CSPU.SYS_LAST_CHG_USER_ID = source.SYS_LAST_CHG_USER_ID , CSPU.SYS_LAST_CHG_PRGRM_ID = source.SYS_LAST_CHG_PRGRM_ID WHEN NOT MATCHED THEN INSERT ( APC_CD , SPMT_PCKG_UNIT_CD , CNPT_CD , RMRK , USE_YN , DEL_YN , SYS_FRST_INPT_DT , SYS_FRST_INPT_USER_ID , SYS_FRST_INPT_PRGRM_ID , SYS_LAST_CHG_DT , SYS_LAST_CHG_USER_ID , SYS_LAST_CHG_PRGRM_ID ) VALUES ( source.APC_CD , source.SPMT_PCKG_UNIT_CD , source.CNPT_CD , source.RMRK , source.USE_YN , source.DEL_YN , SYSDATE , source.SYS_FRST_INPT_USER_ID , source.SYS_FRST_INPT_PRGRM_ID , SYSDATE , source.SYS_LAST_CHG_USER_ID , source.SYS_LAST_CHG_PRGRM_ID );
