mapper.insert:

    	INSERT INTO TB_PLT_BX_INFO
    		(
    			APC_CD
    		  , PLT_BX_SE_CD
    		  , PLT_BX_CD
    		  , PLT_BX_NM
    		  , UNIT_WGHT
    		  , UNIT_CD
    		  , BSS_INVNTR_QNTT
    		  , USE_YN
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{pltBxSeCd}
    		  , FN_GET_ID_PLT_BX(#{apcCd}, #{pltBxSeCd})
    		  , #{pltBxNm}
    		  , #{unitWght}
			  , #{unitCd}
    		  , #{bssInvntrQntt}
    		  , #{useYn}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_PLT_BX_INFO
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , PLT_BX_CD				= #{pltBxCd}
    		 , PLT_BX_NM				= #{pltBxNm}
    		 , UNIT_WGHT				= #{unitWght}
    		 , UNIT_CD					= #{unitCd}
    		 , BSS_INVNTR_QNTT			= #{bssInvntrQntt}
    		 , USE_YN					= #{useYn}
		 WHERE APC_CD 		= #{apcCd}
		   AND PLT_BX_SE_CD = #{pltBkSeCd}
		   AND PLT_BX_CD  	= #{pltBkCd}

mapper.delete:

DELETE FROM TB_PLT_BX_INFO
 WHERE APC_CD		= #{apcCd}
   AND PLT_BX_SE_CD	= #{pltBxSeCd}
   AND PLT_BX_CD	= #{pltBxCd}
