mapper.select:
SELECT TC.APC_CD AS APC_CD , TC.TRSPRT_YMD AS TRSPRT_YMD , TC.VHCLNO AS VHCLNO , TC.SN AS SN , TC.TRSPRT_SE_CD AS TRSPRT_SE_CD , TC.TRSPRT_RGN_CD AS TRSPRT_RGN_CD , TC.WRHS_WGHT AS WRHS_WGHT , TC.TRSPRT_CST AS TRSPRT_CST , TC.BANK_CD AS BANK_CD , TC.ACTNO AS ACTNO , TC.DPSTR AS DPSTR , TC.RMRK AS RMRK , TC.DEL_YN AS DEL_YN , pt0.DRVR_NM AS DRVR_NM FROM TB_RAW_MTR_TRSPRT_CST TC INNER JOIN (SELECT VI.APC_CD, VI.VHCLNO, VI.DRVR_NM FROM TB_WRHS_VHCL_INFO VI) pt0 ON TC.APC_CD = pt0.APC_CD AND TC.VHCLNO = pt0.VHCLNO WHERE TC.APC_CD = #{apcCd}
AND TC.TRSPRT_YMD = #{trsprtYmd}
AND TC.VHCLNO = ( SELECT PI.VHCLNO FROM TB_PRDCR_INFO PI WHERE PI.APC_CD = #{apcCd} AND PI.PRDCR_CD = #{prdcrCd} )
AND TC.VHCLNO = #{vhclno}

mapper.insert:

    	INSERT INTO TB_RGN_TRSPRT_CST_INFO
    		(
    			APC_CD
		   	  , TRSPRT_YMD
		   	  , VHCLNO
		   	  , SN
			  , TRSPRT_SE_CD
			  , TRSPRT_RGN_NM
			  , WRHS_WGHT
    		  , TRSPRT_CST
			  , BANK_CD
			  , ACTNO
			  , DPSTR
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
			  , #{apcCd}
			  , #{trsprtYmd}
			  , #{vhclno}
			  , // SN 함수 만들 예정
			  , #{trsprtSeCd}
			  , #{trsprtRgnNm}
			  , #{wrhsWght}
    		  , #{trsprtCst}
			  , #{bankCd}
			  , #{actno}
			  , #{dpstr}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_RGN_TRSPRT_CST_INFO
		   SET SYS_LAST_CHG_DT			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , TRSPRT_SE_CD				= #{trsprtSeCd}
			 , TRSPRT_RGN_NM			= #{trsprtRgnNm}
			 , WRHS_WGHT				= #{wrhsWght}
    		 , TRSPRT_CST				= #{trsprtCst}
			 , BANK_CD					= #{bankCd}
			 , ACTNO					= #{actno}
			 , DPSTR					= #{dpstr}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD					= #{apcCd}
		   AND TRSPRT_YMD				= #{trsprtYmd}
		   AND VHCLNO					= #{vhclno}
		   AND SN						= #{sn}

mapper.delete:

DELETE FROM TB_RAW_MTR_TRSPRT_CST
 WHERE APC_CD		= #{apcCd}
   AND TRSPRT_YMD	= #{trsprtYmd}
   AND VHCLNO		= #{vhclno}
   AND SN			= #{sn}
