mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_GRD', pt0.GDS_GRD) FROM DUAL) AS GDS_GRD_NM FROM (
SELECT /* com.at.apcss.am.cmns.mapper.SpmtPckgUnitMapper.selectCmnsSpmtPckgUnitList */ SPU.APC_CD AS APC_CD , SPU.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , SPU.SPMT_PCKG_UNIT_NM AS SPMT_PCKG_UNIT_NM , SPU.BRND_NM AS BRND_NM , SPU.ITEM_CD AS ITEM_CD , SPU.VRTY_CD AS VRTY_CD , SPU.SPCFCT_CD AS SPCFCT_CD , (SELECT MAX(X.SPMT_SLS_UNTPRC) KEEP (DENSE_RANK LAST ORDER BY X.APLCN_CRTR_YMD) FROM TB_SPMT_SLS_UNTPRC_REG X WHERE X.APC_CD = SPU.APC_CD AND X.DEL_YN = 'N' AND X.SPMT_PCKG_UNIT_CD = SPU.SPMT_PCKG_UNIT_CD AND X.APLCN_CRTR_YMD <= TO_CHAR(SYSDATE, 'YYYYMMDD')) AS NTSL_UNTPRC , SPU.DEL_YN AS DEL_YN , SPU.GDS_GRD AS GDS_GRD , SC.WGHT AS SPCFCT_WGHT , SC.BX_GDS_QNTT AS BX_GDS_QNTT FROM TB_SPMT_PCKG_UNIT SPU , TB_APC_SPCFCT_CRTR SC WHERE SPU.APC_CD = SC.APC_CD AND SPU.ITEM_CD = SC.ITEM_CD AND SPU.SPCFCT_CD = SC.SPCFCT_CD AND SC.DEL_YN = 'N' AND SPU.DEL_YN = 'N'
AND SPU.APC_CD = #{apcCd}
AND SPU.ITEM_CD = #{itemCd}
AND SPU.VRTY_CD = #{vrtyCd}
AND SPU.SPCFCT_CD = #{spcfctCd}
AND SPU.SPMT_PCKG_UNIT_NM LIKE '%' || #{spmtPckgUnitNm} || '%'
ORDER BY APC_CD, ITEM_CD, SPMT_PCKG_UNIT_CD
) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT /* com.at.apcss.am.cmns.mapper.SpmtPckgUnitMapper.insertSpmtPckgUnitt */
    		INTO TB_SPMT_PCKG_UNIT
			(
				  APC_CD
				, SPMT_PCKG_UNIT_CD
				, SPMT_PCKG_UNIT_NM
				, BRND_NM
				, ITEM_CD
				, VRTY_CD
				, SPCFCT_CD
				, GDS_GRD
				, NTSL_UNTPRC
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				  #{apcCd}
				, #{spmtPckgUnitCd}
	    		, #{spmtPckgUnitNm}
	    		, #{brndNm}
	    		, #{itemCd}
				, #{vrtyCd}
				, #{spcfctCd}
				, #{gdsGrd}
				, #{ntslUntprc}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.cmns.mapper.SpmtPckgUnitMapper.updateSpmtPckgUnit */
			TB_SPMT_PCKG_UNIT
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, SPMT_PCKG_UNIT_NM			= #{spmtPckgUnitNm}
			, BRND_NM					= #{brndNm}
			, ITEM_CD					= #{itemCd}
			, VRTY_CD					= #{vrtyCd}
			, SPCFCT_CD					= #{spcfctCd}
			, GDS_GRD					= #{gdsGrd}
			, NTSL_UNTPRC  				= #{ntslUntprc}
		 WHERE SPMT_PCKG_UNIT_CD 		= #{spmtPckgUnitCd}
		   AND APC_CD					= #{apcCd}


UPDATE TB_SPMT_PCKG_UNIT /* com.at.apcss.am.cmns.mapper.SpmtPckgUnitMapper.deleteSpmtPckgUnit */
   SET DEL_YN 				= 'Y'
WHERE SPMT_PCKG_UNIT_CD 	= #{spmtPckgUnitCd}
  AND APC_CD				= #{apcCd}

mapper.select:

    	 SELECT X.* /* com.at.apcss.am.cmns.mapper.SpmtPckgUnitMapper.spmtPckgUnitDelible */
		   FROM (
		          SELECT
		                 '포장실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                     FROM TB_PCKG_PRFMNC X
		                        , TB_GDS_INVNTR Y
		                    WHERE X.APC_CD = Y.APC_CD
		                      AND X.PCKGNO = Y.PCKGNO
		                      AND X.APC_CD = #{apcCd}
		                      AND Y.ITEM_CD = #{itemCd}
		                      AND Y.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
		                      AND X.DEL_YN = 'N'
		                      AND Y.DEL_YN = 'N'
		                  )  AS CNT
		            FROM DUAL
		          UNION ALL
		          SELECT
		                 '상품재고' AS DELIBLE
		                , (SELECT COUNT(*)
		                     FROM TB_GDS_INVNTR X
		                    WHERE X.APC_CD = #{apcCd}
		                      AND X.ITEM_CD = #{itemCd}
		                      AND X.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
		                      AND X.DEL_YN = 'N'
		                 )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '출하지시' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SPMT_CMND X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.ITEM_CD = #{itemCd}
		                       AND X.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                   '출하실적' AS DELIBLE
		                  , (SELECT COUNT(*)
		                       FROM TB_SPMT_PRFMNC_DTL X
		                      WHERE X.APC_CD = #{apcCd}
		                        AND X.ITEM_CD = #{itemCd}
		                        AND X.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
		                        AND X.DEL_YN = 'N'
		                   )  AS CNT
		             FROM DUAL
		         ) X
		 WHERE CNT > 0
