mapper.insert:

    	INSERT INTO TB_WRHS_VHCL_INFO  /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.insertWrhsVhcl */
    		(
    			APC_CD
    		  , VHCLNO
    		  , DRVR_NM
    		  , BANK_CD
    		  , ACTNO
    		  , DPSTR
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{vhclno}
    		  , #{drvrNm}
    		  , #{bankCd}
    		  , #{actno}
			  , #{dpstr}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_WRHS_VHCL_INFO /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.updateWrhsVhcl */
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , DRVR_NM					= #{drvrNm}
    		 , BANK_CD					= #{bankCd}
    		 , ACTNO					= #{actno}
    		 , DPSTR					= #{dpstr}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND VHCLNO 					= #{vhclno}

mapper.delete:

DELETE FROM TB_WRHS_VHCL_INFO  /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.deleteWrhsVhcl */
 WHERE APC_CD		= #{apcCd}
   AND VHCLNO 		= #{vhclno}
