mapper.insert:

    	INSERT /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.insertWrhsVhcl */
    		INTO TB_WRHS_VHCL_INFO
    		(
    			APC_CD
    		  , VHCLNO
    		  , DRVR_NM
    		  , BANK_CD
    		  , ACTNO
    		  , DPSTR_NM
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{vhclno}
    		  , #{drvrNm}
    		  , #{bankCd}
    		  , FN_UTL_ENCRYPT_ARIA(#{actno})
			  , #{dpstrNm}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.updateWrhsVhcl */
			TB_WRHS_VHCL_INFO
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , DRVR_NM					= #{drvrNm}
    		 , BANK_CD					= #{bankCd}
    		 , ACTNO					= FN_UTL_ENCRYPT_ARIA(#{actno})
    		 , DPSTR_NM					= #{dpstrNm}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND VHCLNO 					= #{vhclno}

mapper.delete:

DELETE FROM TB_WRHS_VHCL_INFO  /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.deleteWrhsVhcl */
 WHERE APC_CD		= #{apcCd}
   AND VHCLNO 		= #{vhclno}

mapper.select:

    		SELECT X.* /* com.at.apcss.am.cmns.mapper.WrhsVhclMapper.vhclDelible */
			  FROM (
			         SELECT
			                '입고실적' AS DELIBLE
			               , (SELECT COUNT(*)
			                    FROM TB_RAW_MTR_WRHS_PRFMNC X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.VHCLNO = #{vhclno}
			                     AND X.DEL_YN = 'N'
			                  )  AS CNT
			           FROM DUAL
			         UNION ALL
			         SELECT
			                '원물운임' AS DELIBLE
			               , (SELECT COUNT(*)
			                    FROM TB_RAW_MTR_TRSPRT_CST X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.VHCLNO = #{vhclno}
			                     AND X.DEL_YN = 'N'
			                  )  AS CNT
			           FROM DUAL
			         UNION ALL
			         SELECT
			                '계량실적' AS DELIBLE
			                , (SELECT COUNT(*)
			                    FROM TB_WGH_PRFMNC_COM X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.VHCLNO = #{vhclno}
			                     AND X.DEL_YN = 'N'
			                  )  AS CNT
			           FROM DUAL
			         UNION ALL
			         SELECT
			                '출하실적' AS DELIBLE
			              , (SELECT COUNT(*)
			                    FROM TB_SPMT_PRFMNC_COM X
			                   WHERE X.APC_CD = #{apcCd}
			                     AND X.VHCLNO = #{vhclno}
			                     AND X.DEL_YN = 'N'
			                 ) AS CNT
			           FROM DUAL
			         ) X
			 WHERE CNT > 0
