mapper.select:
SELECT /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.selectDscdCrtrList */
CC.APC_CD AS APC_CD , CC.DSCD_CRTR_TYPE AS DSCD_CRTR_TYPE , CT.CD_VL_NM AS DSCD_CRTR_TYPE_NM , CT.INDCT_SEQ AS CRTR_TYPE_SEQ , CC.CRTR_CD AS CRTR_CD , CC.CRTR_VL AS CRTR_VL , CC.CRTR_INDCT_NM AS CRTR_INDCT_NM , CC.INDCT_SEQ AS INDCT_SEQ , CC.INDCT_SEQ AS CRTR_SEQ , CC.USE_YN AS USE_YN , CC.CRTR_RMRK AS CRTR_RMRK
FROM TB_INVNTR_DSCD_CRTR CC
JOIN TB_COM_CD_DTL CT ON CT.CD_ID = 'DSCD_CRTR_TYPE' AND CT.APC_CD = '0000' AND CT.CD_VL = CC.DSCD_CRTR_TYPE AND CT.DEL_YN = 'N'
WHERE CC.DEL_YN = 'N'
AND CC.APC_CD = #{apcCd}
AND CC.DSCD_CRTR_TYPE = #{dscdCrtrType}
AND CC.CRTR_CD = #{crtrCd}
AND CC.CRTR_INDCT_NM LIKE '%' || #{crtrIndctNm} || '%'
AND CC.USE_YN = #{useYn}
ORDER BY CRTR_TYPE_SEQ, CRTR_SEQ

SELECT /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.selectDscdCrtrDtl */ CD.APC_CD AS APC_CD , CD.DSCD_CRTR_TYPE AS DSCD_CRTR_TYPE , CD.CRTR_CD AS CRTR_CD , CD.DTL_SN AS DTL_SN , CD.DTL_CD AS DTL_CD , CD.DTL_VL AS DTL_VL , CD.DTL_INDCT_NM AS DTL_INDCT_NM , CD.USE_YN AS USE_YN , CD.USE_YN AS CRTR_DTL_USE_YN , CD.DEL_YN AS DEL_YN
FROM TB_INVNTR_DSCD_CRTR_DTL CD
WHERE CD.APC_CD = #{apcCd} AND CD.DSCD_CRTR_TYPE = #{dscdCrtrType} AND CD.CRTR_CD = #{crtrCd} AND CD.DTL_SN = #{dtlSn}

SELECT /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.selectDscdCrtrDtlList */
CC.APC_CD AS APC_CD , CC.DSCD_CRTR_TYPE AS DSCD_CRTR_TYPE , CT.CD_VL_NM AS DSCD_CRTR_TYPE_NM , CT.INDCT_SEQ AS CRTR_TYPE_SEQ , CC.CRTR_CD AS CRTR_CD , CC.CRTR_VL AS CRTR_VL , CC.CRTR_INDCT_NM AS CRTR_INDCT_NM , CC.INDCT_SEQ AS CRTR_SEQ , CC.USE_YN AS CRTR_USE_YN
, CD.DTL_SN AS DTL_SN , CD.DTL_CD AS DTL_CD , CD.DTL_VL AS DTL_VL , CD.DTL_INDCT_NM AS DTL_INDCT_NM , CD.USE_YN AS CRTR_DTL_USE_YN , CD.INDCT_SEQ AS INDCT_SEQ , CD.INDCT_SEQ AS CRTR_DTL_SEQ , CD.USE_YN AS USE_YN
FROM TB_INVNTR_DSCD_CRTR CC
JOIN TB_COM_CD_DTL CT ON CT.CD_ID = 'DSCD_CRTR_TYPE' AND CT.APC_CD = '0000' AND CT.CD_VL = CC.DSCD_CRTR_TYPE AND CT.DEL_YN = 'N'
JOIN TB_INVNTR_DSCD_CRTR_DTL CD ON CD.APC_CD = CC.APC_CD AND CD.DSCD_CRTR_TYPE = CC.DSCD_CRTR_TYPE AND CD.CRTR_CD = CC.CRTR_CD AND CD.DEL_YN = 'N'
WHERE CC.DEL_YN = 'N'
AND CC.APC_CD = #{apcCd}
AND CC.DSCD_CRTR_TYPE = #{dscdCrtrType}
AND CC.CRTR_CD = #{crtrCd}
AND CC.USE_YN = #{crtrUseYn}
AND CD.USE_YN = #{crtrDtlUseYn}
ORDER BY CRTR_TYPE_SEQ, CRTR_SEQ, CRTR_DTL_SEQ, DTL_SN

SELECT /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.selectCrtrDtlListInUse */ ROW_NUMBER() OVER( PARTITION BY A.APC_CD, A.DSCD_CRTR_TYPE, A.CRTR_CD ORDER BY A.CRTR_DTL_SEQ, A.DTL_SN ASC ) AS SEQ , A.* FROM (
SELECT CC.APC_CD AS APC_CD , CC.DSCD_CRTR_TYPE AS DSCD_CRTR_TYPE , CT.CD_VL_NM AS DSCD_CRTR_TYPE_NM , CT.INDCT_SEQ AS CRTR_TYPE_SEQ , CC.CRTR_CD AS CRTR_CD , CC.CRTR_VL AS CRTR_VL , CC.CRTR_INDCT_NM AS CRTR_INDCT_NM , CC.INDCT_SEQ AS CRTR_SEQ
, CD.DTL_SN AS DTL_SN , CD.DTL_CD AS DTL_CD , CD.DTL_VL AS DTL_VL , CD.DTL_INDCT_NM AS DTL_INDCT_NM , CD.INDCT_SEQ AS CRTR_DTL_SEQ
FROM TB_INVNTR_DSCD_CRTR CC
JOIN TB_COM_CD_DTL CT ON CT.CD_ID = 'DSCD_CRTR_TYPE' AND CT.APC_CD = '0000' AND CT.CD_VL = CC.DSCD_CRTR_TYPE AND CT.DEL_YN = 'N'
LEFT JOIN TB_INVNTR_DSCD_CRTR_DTL CD ON CD.APC_CD = CC.APC_CD AND CD.DSCD_CRTR_TYPE = CC.DSCD_CRTR_TYPE AND CD.CRTR_CD = CC.CRTR_CD AND CD.DEL_YN = 'N' AND CD.USE_YN = 'Y'
WHERE CC.DEL_YN = 'N' AND CC.USE_YN = 'Y'
AND CC.APC_CD = #{apcCd}
AND CC.DSCD_CRTR_TYPE = #{dscdCrtrType}
AND CC.CRTR_CD = #{crtrCd}
AND CD.DTL_SN = #{dtlSn}
ORDER BY CRTR_TYPE_SEQ, CRTR_SEQ, CRTR_DTL_SEQ, DTL_SN ) A

