INSERT /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.insertDscdCrtr */
INTO TB_INVNTR_DSCD_CRTR (
					APC_CD
				  , DSCD_CRTR_TYPE
				  , CRTR_CD
				  , CRTR_VL
				  , CRTR_INDCT_NM
				  , INDCT_SEQ
				  , USE_YN
				  , CRTR_RMRK
				  , DEL_YN
				  , SYS_FRST_INPT_DT
				  , SYS_FRST_INPT_USER_ID
				  , SYS_FRST_INPT_PRGRM_ID
				  , SYS_LAST_CHG_DT
				  , SYS_LAST_CHG_USER_ID
				  , SYS_LAST_CHG_PRGRM_ID
) VALUES (
			 #{apcCd}
		 , #{dscdCrtrType}
		 , #{crtrCd}
		 , #{crtrVl}
		 , #{crtrIndctNm}
		 , #{indctSeq}
		 , #{useYn}
		 , #{crtrRmrk}
		 , 'N'
		 , SYSDATE
		 , #{sysFrstInptUserId}
		 , #{sysFrstInptPrgrmId}
		 , SYSDATE
		 , #{sysLastChgUserId}
		 , #{sysLastChgPrgrmId}
		 )

mapper.update:

UPDATE /* com.at.apcss.am.dscd.mapper.DscdCrtrMapper.updateDscdCrtr */
	TB_INVNTR_DSCD_CRTR
SET
	SYS_LAST_CHG_DT 			= SYSDATE
  , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
  , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}

  , CRTR_VL			= #{crtrVl}
  , CRTR_INDCT_NM		= #{crtrIndctNm}
  , INDCT_SEQ			= #{indctSeq}
  , USE_YN			= #{useYn}
  , CRTR_RMRK			= #{crtrRmrk}

WHERE APC_CD = #{apcCd}
  AND DSCD_CRTR_TYPE = #{dscdCrtrType}
  AND CRTR_CD = #{crtrCd}

