mapper.select:
SELECT
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT X.APC_NM FROM TB_APC_INFO X WHERE X.APC_CD = pt0.APC_CD) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_GDS(pt0.APC_CD, pt0.ITEM_CD, pt0.GDS_GRD) FROM DUAL) AS GDS_GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('PCKG_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.SORT_GRD_CD) FROM DUAL) AS SORT_GRD_NM , pt0.PCKGNO || '-' || pt0.PCKG_SN AS PCKGNO_INDCT FROM ( SELECT /* com.at.apcss.am.invntr.mapper.GdsInvntrMapper.selectGdsInvntrList */ GI.APC_CD AS APC_CD , GI.PCKGNO AS PCKGNO , GI.PCKG_SN AS PCKG_SN , GI.PCKG_YMD AS PCKG_YMD , GI.FCLT_CD AS FCLT_CD , GI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , GI.ITEM_CD AS ITEM_CD , GI.VRTY_CD AS VRTY_CD , GI.SPCFCT_CD AS SPCFCT_CD , GI.GDS_GRD AS GDS_GRD , GI.GDS_CD AS GDS_CD , GI.GDS_SE_CD AS GDS_SE_CD , GI.PRDCTN_YR AS PRDCTN_YR , GI.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , GI.PCKG_QNTT AS PCKG_QNTT , GI.PCKG_WGHT AS PCKG_WGHT , GI.SPMT_QNTT AS SPMT_QNTT , GI.SPMT_WGHT AS SPMT_WGHT , GI.INVNTR_QNTT AS INVNTR_QNTT , GI.INVNTR_WGHT AS INVNTR_WGHT , GI.PLOR_CD AS PLOR_CD , GI.DEL_YN AS DEL_YN , GI.RMRK AS RMRK , (SELECT BRND_NM FROM TB_SPMT_PCKG_UNIT X WHERE X.APC_CD = GI.APC_CD AND X.ITEM_CD = GI.ITEM_CD AND X.VRTY_CD = GI.VRTY_CD AND X.SPCFCT_CD = GI.SPCFCT_CD AND X.SPMT_PCKG_UNIT_CD = GI.SPMT_PCKG_UNIT_CD AND X.DEL_YN = 'N' ) AS BRND_NM , (SELECT MAX(GRD_CD) FROM TB_SORT_INVNTR SI , TB_PCKG_INPT_PRFMNC IP WHERE SI.DEL_YN = 'N' AND IP.DEL_YN = 'N' AND SI.SORTNO = IP.SORTNO AND SI.SORT_SN = IP.SORT_SN AND IP.PCKGNO = GI.PCKGNO ) AS SORT_GRD_CD FROM TB_GDS_INVNTR GI WHERE GI.DEL_YN = 'N' AND GI.INVNTR_WGHT > 0
AND GI.APC_CD = #{apcCd}
AND GI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND GI.PCKG_YMD >= #{pckgYmdFrom}
AND GI.PCKG_YMD <= #{pckgYmdTo}
AND GI.ITEM_CD = #{itemCd}
AND GI.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND GI.SPCFCT_CD = #{spcfctCd}
AND GI.GDS_GRD = #{gdsGrd}
AND GI.GDS_SE_CD = #{gdsSeCd}
AND GI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND GI.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
AND GI.PCKG_YMD = #{crtrYmd}
AND GI.RPRS_PRDCR_CD = #{prdcrCd}
AND GI.GDS_SE_CD = #{gdsSeCd}
AND GI.WRHS_SE_CD = #{wrhsSeCd}
ORDER BY PCKG_YMD, PCKGNO, PCKG_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

