mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.invntr.mapper.GdsInvntrMapper.selectGdsInvntrList */ GI.APC_CD AS APC_CD , GI.PCKGNO AS PCKGNO , GI.PCKG_SN AS PCKG_SN , GI.PCKG_YMD AS PCKG_YMD , GI.FCLT_CD AS FCLT_CD , GI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , GI.ITEM_CD AS ITEM_CD , GI.VRTY_CD AS VRTY_CD , GI.SPCFCT_CD AS SPCFCT_CD , GI.GDS_GRD AS GDS_GRD , GI.PCKG_SE_CD AS PCKG_SE_CD , GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , GI.PCKG_QNTT AS PCKG_QNTT , GI.PCKG_WGHT AS PCKG_WGHT , GI.SPMT_QNTT AS SPMT_QNTT , GI.SPMT_WGHT AS SPMT_WGHT , GI.INVNTR_QNTT AS INVNTR_QNTT , GI.INVNTR_WGHT AS INVNTR_WGHT , GI.DEL_YN AS DEL_YN FROM TB_GDS_INVNTR GI
AND GI.APC_CD = #{apcCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

