AND DRI.GDS_SE_CD = #{gdsSeCd}
AND DRI.WRHS_SE_CD = #{wrhsSeCd}
AND DRI.TRSPRT_SE_CD = #{trsprtSeCd}
AND DRI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND DRI.BX_KND = #{bxKnd}
AND DRI.PRDCR_CD = #{prdcr}
AND DRI.GDS_SE_CD = #{gdsSe}
AND DRI.WRHS_SE_CD = #{wrhsSe}
