AND SI.APC_CD = #{apcCd}
AND SI.SORTNO = #{sortno}
AND SI.INPT_YMD = #{sortYmd}
AND SI.INPT_YMD >= #{sortYmdFrom}
AND SI.INPT_YMD <= #{sortYmdTo}
AND SI.FCLT_CD = #{sortFcltCd}
AND SI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND SI.ITEM_CD = #{itemCd}
AND SI.VRTY_CD = #{vrtyCd}
AND SI.SPCFCT_CD = #{spcfctCd}
AND SI.GRD_CD = #{grdCd}
AND SI.GDS_SE_CD = #{gdsSeCd}
AND SI.WRHS_SE_CD = #{wrhsSeCd}
AND SI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND SI.PRDCTN_YR = #{prdctnYr}
ORDER BY SI.APC_CD, SI.INPT_YMD, SI.SORTNO ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

