				, SORT_WGHT
				, PCKG_QNTT
				, PCKG_WGHT
				, INVNTR_QNTT
				, INVNTR_WGHT
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
