mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* FROM (
SELECT /* com.at.apcss.am.oprtr.mapper.OprtrMapper.selectOprtrList */ OM.APC_CD AS APC_CD , OM.FLNM AS FLNM , OM.BRDT AS BRDT , OM.TELNO AS TELNO , OM.ADDR AS ADDR , OM.JNCMP_YMD AS JNCMP_YMD , OM.BANK_CD AS BANK_CD , (SELECT FN_UTL_DECRYPT_ARIA(OM.ACTNO) FROM DUAL) AS ACTNO , OM.DPSTR_NM AS DPSTR_NM , OM.RMRK AS RMRK , OM.DEL_YN AS DEL_YN FROM TB_OPRTR_MNG OM WHERE OM.APC_CD = #{apcCd}
AND OM.FLNM LIKE '%' || #{flnm} || '%'
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT INTO TB_OPRTR_MNG
    		(
    			APC_CD
    		  , FLNM
		      , BRDT
		      , TELNO
		      , ADDR
		      , JNCMP_YMD
		      , BANK_CD
		      , ACTNO
		      , DPSTR_NM
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{flnm}
    		  , #{brdt}
    		  , #{telno}
    		  , #{addr}
    		  , #{jncmpYmd}
    		  , #{bankCd}
    		  , FN_UTL_ENCRYPT_ARIA(#{actno})
    		  , #{dpstrNm}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_OPRTR_MNG
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
    		 , TELNO					= #{telno}
    		 , ADDR						= #{addr}
    		 , JNCMP_YMD				= #{jncmpYmd}
    		 , BANK_CD					= #{bankCd}
    		 , ACTNO					= FN_UTL_ENCRYPT_ARIA(#{actno})
    		 , DPSTR_NM					= #{dpstrNm}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND FLNM 					= #{flnm}
		   AND BRDT 					= #{brdt}

mapper.delete:

		DELETE FROM TB_OPRTR_MNG
		 WHERE APC_CD			= #{apcCd}
		   AND FLNM 			= #{flnm}
 		   AND BRDT 			= #{brdt}

mapper.select:

    	SELECT X.* /* com.at.apcss.am.oprtr.mapper.OprtrMapper.oprtrDelible */
		  FROM (
		           SELECT
		                  '선별작업실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_OPRTR_SORT_PRFMNC X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.BRDT   = #{brdt}
		                       AND X.FLNM   = #{flnm}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '포장작업실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_OPRTR_PCKG_PRFMNC X
		                     WHERE X.APC_CD = #{apcCd}
		                       AND X.BRDT   = #{brdt}
		                       AND X.FLNM   = #{flnm}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		         ) X
		 WHERE CNT > 0
