mapper.insert:

    	INSERT INTO TB_OPRTR_MNG
    		(
    			APC_CD
    		  , FLNM
		      , BRDT
		      , TELNO
		      , ADDR
		      , JNCMP
		      , BANK_CD
		      , ACTNO
		      , DPSTR
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{flnm}
    		  , #{brdt}
    		  , #{telno}
    		  , #{addr}
    		  , #{jncmp}
    		  , #{bankCd}
    		  , #{actno}
    		  , #{dpstr}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_OPRTR_MNG
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
    		 , TELNO					= #{telno}
    		 , ADDR						= #{addr}
    		 , JNCMP					= #{jncmp}
    		 , BANK_CD					= #{bankCd}
    		 , ACTNO					= #{actno}
    		 , DPSTR					= #{dpstr}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND FLNM 					= #{flnm}
		   AND BRDT 					= #{brdt}

mapper.delete:

		DELETE FROM TB_OPRTR_MNG
		 WHERE APC_CD			= #{apcCd}
		   AND FLNM 			= #{flnm}
 		   AND BRDT 			= #{brdt}
