mapper.select:
SELECT OP.SORTNO AS PRFMNCNO , (SELECT FN_GET_COM_CD_VL_NM('PRFMNC_SE_CD', '2') FROM DUAL) AS PRFMNC_SE_NM , '2' AS PRFMNC_SE_CD , (SELECT FN_GET_COM_CD_VL_NM( 'SORT_FCLT_CD' , (SELECT FCLT_CD FROM TB_SORT_PRFMNC X WHERE X.APC_CD = OP.APC_CD AND X.SORTNO = OP.SORTNO ) , OP.APC_CD) FROM DUAL) AS FCLT_NM
SELECT OP.PCKGNO AS PRFMNCNO , (SELECT FN_GET_COM_CD_VL_NM('PRFMNC_SE_CD', '3') FROM DUAL) AS PRFMNC_SE_NM , '3' AS PRFMNC_SE_CD , (SELECT FN_GET_COM_CD_VL_NM( 'PCKG_FCLT_CD' , (SELECT MAX(FCLT_CD) FROM TB_GDS_INVNTR X WHERE X.APC_CD = OP.APC_CD AND X.PCKGNO = OP.PCKGNO ) , OP.APC_CD) FROM DUAL) AS FCLT_NM
, OP.APC_CD AS APC_CD , OP.PRFMNC_SN AS PRFMNC_SN , OP.BRDT AS BRDT , OP.FLNM AS FLNM , OP.JOB_YMD AS JOB_YMD , OP.JOB_BGNG_HR AS JOB_BGNG_HR , OP.JOB_END_HR AS JOB_END_HR , OP.JOB_HR AS JOB_HR , OP.RMRK AS RMRK , OP.DEL_YN AS DEL_YN
FROM TB_OPRTR_SORT_PRFMNC OP
FROM TB_OPRTR_PCKG_PRFMNC OP
WHERE OP.APC_CD = #{apcCd} AND OP.DEL_YN = 'N' AND OP.JOB_YMD = #{jobYmd}
AND OP.SORTNO = #{prfmncno}
AND OP.PCKGNO = #{prfmncno}

mapper.insert:

    		INSERT /* com.at.apcss.am.oprtr.mapper.OprtrPrfmncMapper.insertOprtrPrfmnc */




			     INTO TB_OPRTR_SORT_PRFMNC
			  	    ( SORTNO


			     INTO TB_OPRTR_PCKG_PRFMNC
			  	    ( PCKGNO



    		, APC_CD
			        , PRFMNC_SN
			        , BRDT
			        , FLNM
			        , JOB_YMD
			        , JOB_BGNG_HR
			        , JOB_END_HR
			        , JOB_HR
			        , RMRK
			        , SYS_FRST_INPT_DT
			        , SYS_FRST_INPT_USER_ID
			        , SYS_FRST_INPT_PRGRM_ID
			        , SYS_LAST_CHG_DT
			        , SYS_LAST_CHG_USER_ID
			        , SYS_LAST_CHG_PRGRM_ID
			        )
			 VALUES
			        (
			          #{prfmncno}
			        , #{apcCd}
			        , #{prfmncSn}
			        , #{brdt}
			        , #{flnm}
			        , #{jobYmd}
			        , #{jobBgngHr}
			        , #{jobEndHr}
			        , #{jobHr}
			        , #{rmrk}
			        , SYSDATE
			        , #{sysFrstInptUserId}
			        , #{sysFrstInptPrgrmId}
			        , SYSDATE
			        , #{sysLastChgUserId}
			        , #{sysLastChgPrgrmId}
			        )

mapper.update:

		UPDATE




			   TB_OPRTR_SORT_PRFMNC


			   TB_OPRTR_PCKG_PRFMNC




		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
    		 , BRDT						= #{brdt}
	         , FLNM						= #{flnm}
	         , JOB_YMD					= #{jobYmd}
	         , JOB_BGNG_HR				= #{jobBgngHr}
	         , JOB_END_HR				= #{jobEndHr}
	         , JOB_HR					= #{jobHr}
	         , RMRK						= #{rmrk}
		 WHERE APC_CD 					= #{apcCd}
		   AND PRFMNC_SN 				= #{prfmncSn}




		   AND SORTNO					= #{prfmncno}


		   AND PCKGNO					= #{prfmncno}

mapper.delete:

DELETE FROM




	   TB_OPRTR_SORT_PRFMNC
 WHERE SORTNO			= #{prfmncno}


	   TB_OPRTR_PCKG_PRFMNC
 WHERE PCKGNO			= #{prfmncno}




   AND APC_CD 			= #{apcCd}
   AND PRFMNC_SN 		= #{prfmncSn}

mapper.select:

SELECT
	   FN_GET_ID_OPRTR_PRFMNC_SN(
	   						     #{apcCd}
	   						   , #{prfmncSeCd}
	   						   , #{prfmncno}
	   						   ) AS PRFMNC_SN
  FROM DUAL
