mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM , (SELECT FN_GET_COM_CD_VL_NM('OUTORDR_TYPE', pt0.OUTORDR_TYPE) FROM DUAL) AS OUTORDR_TYPE_NM , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS RCPT_CFMTN_APC_NM FROM (
SELECT pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM , (SELECT FN_GET_COM_CD_VL_NM('OUTORDR_TYPE', pt0.OUTORDR_TYPE) FROM DUAL) AS OUTORDR_TYPE_NM , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS RCPT_CFMTN_APC_NM FROM (
SELECT /* com.at.apcss.am.ordr.mapper.OrdrMapper.selectOrdrList */ OI.APC_CD AS APC_CD , OI.APC_SE_CD AS APC_SE_CD , OI.RCPT_CFMTN_YMD AS RCPT_CFMTN_YMD , OI.OUTORDR_TYPE AS OUTORDR_TYPE , OI.OUTORDRNO AS OUTORDRNO , OI.APC_CNPT_NM AS APC_CNPT_NM , OI.APC_CNPT_CD AS APC_CNPT_CD , OI.WRHS_YMD AS WRHS_YMD , OI.OUTORDR_YMD AS OUTORDR_YMD , OI.OUTORDR_PRSN AS OUTORDR_PRSN , OI.SPLY_PRSN AS SPLY_PRSN , OI.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , OI.DLDTN AS DLDTN , OI.ITEM_CD AS ITEM_CD , OI.VRTY_CD AS VRTY_CD , OI.SPCFCT_CD AS SPCFCT_CD , OI.BX_GDS_QNTT AS BX_GDS_QNTT , OI.OUTORDR_QNTT AS OUTORDR_QNTT , OI.PIECE_QNTT AS PIECE_QNTT , OI.UNIT_CD AS UNIT_CD , OI.BX_UNTPRC AS BX_UNTPRC , OI.OUTORDR_UNTPRC AS OUTORDR_UNTPRC , OI.OUTORDR_UNIT_CD AS OUTORDR_UNIT_CD , OI.LOT AS LOT , OI.TX_AMT AS TX_AMT , OI.OUTORDR_AMT AS OUTORDR_AMT , OI.WRHS_TYPE AS WRHS_TYPE , OI.DOCNO AS DOCNO , OI.DOCK_INFO AS DOCK_INFO , OI.CNTR_SE AS CNTR_SE , OI.BUYER_NM AS BUYER_NM , OI.CNTR_RCVD_WRHS_YMD AS CNTR_RCVD_WRHS_YMD , OI.CNTR_NM AS CNTR_NM , OI.DLVGDS_SE_CD AS DLVGDS_SE_CD , OI.ETC AS ETC , OI.RCPT_CFMTN_APC_CD AS RCPT_CFMTN_APC_CD , OI.SPMT_CMNDNO AS SPMT_CMNDNO , SC.SPMT_YMD AS SPMT_YMD , (SELECT GDS_GRD FROM TB_SPMT_PCKG_UNIT X WHERE X.APC_CD = OI.APC_CD AND X.SPMT_PCKG_UNIT_CD = OI.SPMT_PCKG_UNIT_CD AND X.DEL_YN = 'N' ) AS GDS_GRD , (SELECT WGHT FROM TB_APC_SPCFCT_CRTR X WHERE X.APC_CD = OI.APC_CD AND X.ITEM_CD = OI.ITEM_CD AND X.SPCFCT_CD = OI.SPCFCT_CD AND X.DEL_YN = 'N' ) AS WGHT , OI.DEL_YN AS DEL_YN FROM TB_OUTORDR_INFO OI , TB_SPMT_PRFMNC_COM SC , TB_SPMT_PRFMNC_DTL SD WHERE OI.APC_CD = SC.APC_CD AND OI.APC_CD = SD.APC_CD AND OI.ITEM_CD = SD.ITEM_CD AND OI.VRTY_CD = SD.VRTY_CD AND OI.SPCFCT_CD = SD.SPCFCT_CD AND OI.SPMT_PCKG_UNIT_CD = SD.SPMT_PCKG_UNIT_CD AND OI.OUTORDRNO = SD.OUTORDRNO AND SC.SPMTNO = SD.SPMTNO AND OI.APC_CNPT_CD = SC.CNPT_CD AND OI.DEL_YN = 'N' AND SC.DEL_YN = 'N' AND SD.DEL_YN = 'N'
AND (( (OI.APC_SE_CD = '1' AND OI.APC_CD = '0000' AND OI.RCPT_CFMTN_APC_CD = #{apcCd}) OR (OI.APC_SE_CD = '2' AND OI.APC_CD = #{apcCd})) AND OI.RCPT_CFMTN_YMD IS NOT NULL)
AND (( (OI.APC_SE_CD = '1' AND OI.APC_CD = '0000') OR (OI.APC_SE_CD = '2' AND OI.APC_CD = #{apcCd})) AND OI.RCPT_CFMTN_YMD IS NULL)
AND ((( (OI.APC_SE_CD = '1' AND OI.APC_CD = '0000') OR (OI.APC_SE_CD = '2' AND OI.APC_CD = #{apcCd})) AND OI.RCPT_CFMTN_YMD IS NULL) OR (( (OI.APC_SE_CD = '1' AND OI.APC_CD = '0000' AND OI.RCPT_CFMTN_APC_CD = #{apcCd}) OR (OI.APC_SE_CD = '2' AND OI.APC_CD = #{apcCd})) AND OI.RCPT_CFMTN_YMD IS NOT NULL))
AND OI.OUTORDR_YMD >= #{outordrYmdFrom}
AND OI.OUTORDR_YMD <= #{outordrYmdTo}
AND OI.OUTORDR_TYPE = #{outordrType}
AND OI.APC_CNPT_NM LIKE '%' || #{apcCnptNm} || '%'
AND OI.ITEM_CD = #{itemCd}
AND OI.VRTY_CD = #{vrtyCd}
AND OI.WRHS_YMD = #{wrhsYmd}
AND (SELECT FN_GET_SPMT_PCKG_UNIT_NM(OI.SPMT_PCKG_UNIT_CD, OI.APC_CD) FROM DUAL) LIKE '%' || #{spmtPckgUnitNm} || '%'
GROUP BY OI.APC_CD , OI.APC_SE_CD , OI.RCPT_CFMTN_YMD , OI.OUTORDR_TYPE , OI.OUTORDRNO , OI.APC_CNPT_NM , OI.APC_CNPT_CD , OI.WRHS_YMD , OI.OUTORDR_YMD , OI.OUTORDR_PRSN , OI.OUTORDR_PRSN_CD , OI.SPLY_PRSN , OI.SPMT_PCKG_UNIT_CD , OI.DLDTN , OI.DLDTN_CD , OI.ITEM_CD , OI.VRTY_CD , OI.SPCFCT_CD , OI.BX_GDS_QNTT , OI.OUTORDR_QNTT , OI.PIECE_QNTT , OI.UNIT_CD , OI.BX_UNTPRC , OI.OUTORDR_UNTPRC , OI.OUTORDR_UNIT_CD , OI.LOT , OI.TX_AMT , OI.OUTORDR_AMT , OI.WRHS_TYPE , OI.DOCNO , OI.DOCK_INFO , OI.CNTR_SE , OI.BUYER_NM , OI.CNTR_RCVD_WRHS_YMD , OI.CNTR_NM , OI.CNTR_CD , OI.DLVGDS_SE_CD , OI.ETC , OI.RCPT_CFMTN_APC_CD , OI.SPMT_CMNDNO , SC.SPMT_YMD , OI.DEL_YN
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

