mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* ,(SELECT FN_GET_COM_CD_VL_NM('FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM ,(SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM ,(SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM ,(SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM FROM (
SELECT pt0.* ,(SELECT FN_GET_COM_CD_VL_NM('FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM ,(SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM ,(SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM ,(SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM FROM (
SELECT /* com.at.apcss.am.pckg.mapper.PckgCmndMapper.selectPckgCmndList */ PC.APC_CD AS APC_CD ,PC.PCKG_CMNDNO AS PCKG_CMNDNO ,PC.PCKG_CMND_SN AS PCKG_CMND_SN ,PC.PCKG_CMND_YMD AS PCKG_CMND_YMD ,PC.FCLT_CD AS FCLT_CD ,PC.CNPT_CD AS CNPT_CD ,PC.DUDT_YMD AS DUDT_YMD ,PC.ITEM_CD AS ITEM_CD ,PC.VRTY_CD AS VRTY_CD ,PC.SPCFCT_CD AS SPCFCT_CD ,PC.OUTORDR_QNTT AS OUTORDR_QNTT ,PC.OUTORDR_WGHT AS OUTORDR_WGHT ,PC.BX_GDS_QNTT AS BX_GDS_QNTT ,PC.CMND_QNTT AS CMND_QNTT ,PC.CMND_WGHT AS CMND_WGHT ,PP.PCKG_QNTT AS PCKG_QNTT ,PP.PCKG_WGHT AS PCKG_WGHT ,PC.OUTORDRNO AS OUTORDRNO ,PC.GDS_CD AS GDS_CD ,PC.RMRK AS RMRK ,PC.DEL_YN AS DEL_YN FROM TB_PCKG_CMND PC , TB_PCKG_PRFMNC PP WHERE PC.APC_CD = #{apcCd} AND PC.APC_CD = PP.APC_CD(+) AND PC.PCKG_CMNDNO = PP.PCKG_CMNDNO(+)
AND PC.PCKG_CMND_YMD = #{pckgCmndYmd}
AND PC.PCKG_CMND_YMD >= #{pckgCmndFromYmd}
AND PC.PCKG_CMND_YMD <= #{pckgCmndToYmd}
AND PC.FCLT_CD = #{fcltCd}
AND PC.CNPT_CD = #{cnptCd}
AND PC.DUDT_YMD = #{dudtYmd}
AND PC.ITEM_CD = #{itemCd}
AND PC.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND PC.SPCFCT_CD = #{spcfctCd}
AND PC.OUTORDRNO = #{outordrno}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

mapper.insert:

   			INSERT  /* com.at.apcss.am.pckg.mapper.PckgCmndMapper.insertPckgInpt */
   			INTO TB_PCKG_CMND
