				, DUDT_YMD
				, ITEM_CD
				, VRTY_CD
				, SPCFCT_CD
				, OUTORDR_QNTT
				, OUTORDR_WGHT
				, BX_GDS_QNTT
				, CMND_QNTT
				, CMND_WGHT
				, OUTORDRNO
				, GDS_CD
				, RMRK
				, DEL_YN
				, SYS_FRST_INPT_DT
				, SYS_FRST_INPT_USER_ID
				, SYS_FRST_INPT_PRGRM_ID
				, SYS_LAST_CHG_DT
				, SYS_LAST_CHG_USER_ID
				, SYS_LAST_CHG_PRGRM_ID
			)
			VALUES
			(
				#{apcCd}
