				, #{sysLastChgUserId}
				, #{sysLastChgPrgrmId}
			)

mapper.delete:

    	DELETE /* com.at.apcss.am.pckg.mapper.PckgCmndMapper.deletePckgInpt */
		  FROM TB_PCKG_CMND
		 WHERE APC_CD = #{apcCd}
		   AND PCKG_CMNDNO = #{pckgCmndno}
		   AND PCKG_CMND_SN = #{pckgCmndSn}
