mapper.select:
SELECT /* com.at.apcss.am.pckg.mapper.PckgInptMapper.selectPckgInptList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS RPRS_PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('PCKG_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD, pt0.APC_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM ( SELECT /* com.at.apcss.am.pckg.mapper.PckgInptMapper.selectPckgInptList */ PI.APC_CD AS APC_CD , PI.SORTNO AS SORTNO , PI.SORT_SN AS SORT_SN , PI.INPT_SN AS INPT_SN , PI.PCKGNO AS PCKGNO , PI.PCKG_CMNDNO AS PCKG_CMNDNO , PI.INPT_YMD AS INPT_YMD , PI.QNTT AS QNTT , PI.WGHT AS WGHT , PI.RMRK AS RMRK , PI.FCLT_CD AS FCLT_CD , SI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , SI.ITEM_CD AS ITEM_CD , SI.VRTY_CD AS VRTY_CD , SI.SPCFCT_CD AS SPCFCT_CD , SI.GRD_CD AS GRD_CD , SI.GDS_SE_CD AS GDS_SE_CD , SI.PRDCTN_YR AS PRDCTN_YR , SI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD FROM TB_PCKG_INPT_PRFMNC PI , TB_SORT_INVNTR SI WHERE PI.APC_CD = SI.APC_CD AND PI.SORTNO = SI.SORTNO AND PI.SORT_SN = SI.SORT_SN AND PI.APC_CD = #{apcCd} AND PI.DEL_YN = 'N' AND SI.DEL_YN = 'N'
AND PI.SORTNO = #{sortno}
AND PI.SORT_SN = #{sortSn}
AND PI.PCKGNO = #{pckgno}
AND PI.INPT_YMD = #{inptYmd}
AND PI.FCLT_CD = #{fcltCd}
AND PI.PCKGNO IS NULL
AND PI.PCKGNO IS NOT NULL
ORDER BY INPT_YMD, SORTNO, SORT_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT /* com.at.apcss.am.pckg.mapper.PckgInptMapper.insertPckgInpt */
    		INTO TB_PCKG_INPT_PRFMNC (
				  APC_CD
				, SORTNO
				, SORT_SN
				, INPT_SN
				, INPT_YMD
				, QNTT
				, WGHT
				, FCLT_CD
				, PCKGNO
				, PCKG_CMNDNO
				, RMRK
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
				  #{apcCd}
				, #{sortno}
				, #{sortSn}
				, NVL((
					SELECT MAX(INPT_SN)
					  FROM TB_PCKG_INPT_PRFMNC
					 WHERE APC_CD = #{apcCd}
					   AND SORTNO = #{sortno}
					   AND SORT_SN = #{sortSn}
				  ), 0) + 1
				, #{inptYmd}
				, #{qntt}
				, #{wght}
				, #{fcltCd}
				, #{pckgno}
				, #{pckgCmndno}
				, #{rmrk}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgInptMapper.updatePckgInpt */
