    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, PCKGNO 	= #{pckgno}
			, RMRK 		= #{rmrk}


		 WHERE APC_CD = #{apcCd}
		   AND SORTNO = #{sortno}
		   AND SORT_SN = #{sortSn}
		   AND PCKGNO IS NULL
		   AND DEL_YN = 'N'

mapper.delete:

