mapper.select:
SELECT /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.selectPckgPrfmncList */ GI.FCLT_CD AS FCLT_CD , (SELECT FN_GET_COM_CD_VL_NM('FCLT_CD', GI.FCLT_CD, GI.APC_CD) FROM DUAL) AS FCLT_NM ,GI.PCKG_YMD AS PCKG_YMD ,PP.PCKG_CMNDNO AS PCKG_CMNDNO ,GI.VRTY_CD AS VRTY_CD , (SELECT FN_GET_VRTY_NM(GI.ITEM_CD,GI.VRTY_CD,GI.APC_CD) FROM DUAL) AS VRTY_NM ,GI.SPCFCT_CD AS SPCFCT_CD , (SELECT FN_GET_SPCFCT_NM(GI.ITEM_CD,GI.SPCFCT_CD,GI.APC_CD) FROM DUAL) AS SPCFCT_NM ,GI.GDS_GRD AS GDS_GRD , (SELECT FN_GET_COM_CD_VL_NM('GDS_GRD', GI.GDS_GRD, GI.APC_CD) FROM DUAL) AS GDS_GRD_NM ,PP.PCKG_WGHT AS PCKG_WGHT ,GI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , (SELECT FN_GET_PRDCR_NM(GI.RPRS_PRDCR_CD,GI.APC_CD) FROM DUAL) AS RPRS_PRDCR_NM ,GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', GI.WAREHOUSE_SE_CD, GI.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM ,GI.PCKG_QNTT AS PCKG_QNTT ,GI.PCKG_WGHT AS PCKG_WGHT ,GI.SPMT_QNTT AS SPMT_QNTT ,GI.SPMT_WGHT AS SPMT_WGHT FROM TB_PCKG_PRFMNC PP, TB_GDS_INVNTR GI WHERE PP.APC_CD = GI.APC_CD
AND GI.PCKG_YMD >= #{fromPckgYmd}
AND GI.PCKG_YMD <= #{toPckgYmd}
AND GI.VRTY_CD = #{vrtyCd}
AND GI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND GI.FCLT_CD = #{fcltCd}
AND GI.GDS_GRD = #{grdCd}
AND GI.WAREHOUSE_SE_CD = #{strgWarehouse}

mapper.insert:

    	INSERT /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.insertPckgPrfmnc */
    		INTO TB_PCKG_PRFMNC (
				  APC_CD
				, PCKGNO
				, PCKG_SN
				, PCKG_YMD
				, PCKG_QNTT
				, PCKG_WGHT
				, WAREHOUSE_SE_CD
				, PCKG_CMNDNO
				, RMRK
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
				  #{apcCd}
				, #{pckgno}
				, #{pckgSn}
				, #{pckgYmd}
				, #{pckgQntt}
				, #{pckgWght}
				, #{warehouseSeCd}
				, #{pckgCmndno}
				, #{rmrk}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updatePckgPrfmnc */
			TB_PCKG_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, PCKG_YMD 			= #{pckgYmd}
			, PCKG_QNTT 		= #{pckgQntt}
			, PCKG_WGHT 		= #{pckgWght}
			, WAREHOUSE_SE_CD	= #{warehouseSeCd}
			, RMRK 				= #{rmrk}
			, PCKG_CMNDNO 		= #{pckgCmndno}


			, DEL_YN			= #{delYn}


		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}

mapper.delete:

    	DELETE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.deletePckgPrfmnc */
		  FROM TB_PCKG_PRFMNC
		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updateInptPckgno */
			TB_PCKG_INPT_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}

			, PCKGNO = #{pckgno}

		 WHERE APC_CD = #{apcCd}
		   AND SORTNO = #{sortno}
		   AND GRD_CD = #{grdCd}
		   AND PCKGNO IS NULL
