mapper.select:
SELECT /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.selectPckgPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_APC_NM(pt0.APC_CD) FROM DUAL) AS APC_NM , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS RPRS_PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_GRD_NM_GDS(pt0.APC_CD, pt0.ITEM_CD, pt0.GDS_GRD) FROM DUAL) AS GDS_GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('PCKG_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD, pt0.APC_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM , (SELECT MAX(X.PCKG_CMNDNO) FROM TB_PCKG_INPT_PRFMNC X WHERE X.APC_CD = pt0.APC_CD AND X.PCKGNO = pt0.PCKGNO AND X.DEL_YN = 'N') AS PCKG_CMNDNO , pt0.PCKGNO || '-' || pt0.PCKG_SN AS PCKGNO_INDCT FROM ( SELECT GI.APC_CD AS APC_CD , GI.PCKGNO AS PCKGNO , GI.PCKG_SN AS PCKG_SN , GI.PCKG_YMD AS PCKG_YMD , GI.FCLT_CD AS FCLT_CD , GI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , GI.ITEM_CD AS ITEM_CD , GI.VRTY_CD AS VRTY_CD , GI.SPCFCT_CD AS SPCFCT_CD , GI.GDS_GRD AS GDS_GRD , GI.GDS_SE_CD AS GDS_SE_CD , GI.PRDCTN_YR AS PRDCTN_YR , GI.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , GI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , GI.PCKG_QNTT AS PCKG_QNTT , GI.PCKG_WGHT AS PCKG_WGHT , GI.SPMT_QNTT AS SPMT_QNTT , GI.SPMT_WGHT AS SPMT_WGHT , GI.INVNTR_QNTT AS INVNTR_QNTT , GI.INVNTR_WGHT AS INVNTR_WGHT , GI.PLOR_CD AS PLOR_CD , GI.DEL_YN AS DEL_YN
FROM TB_GDS_INVNTR GI , TB_PCKG_PRFMNC PP
WHERE GI.APC_CD = PP.APC_CD AND GI.PCKGNO = PP.PCKGNO AND GI.PCKG_SN = PP.PCKG_SN AND GI.DEL_YN = 'N' AND PP.DEL_YN = 'N'
AND GI.APC_CD = #{apcCd}
AND GI.PCKGNO = #{pckgno}
AND GI.PCKG_YMD = #{pckgYmd}
AND GI.PCKG_YMD >= #{pckgYmdFrom}
AND GI.PCKG_YMD <= #{pckgYmdTo}
AND GI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND GI.FCLT_CD = #{fcltCd}
AND GI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND GI.GDS_GRD = #{gdsGrd}
AND GI.ITEM_CD = #{itemCd}
AND GI.VRTY_CD IN (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND GI.SPCFCT_CD = #{spcfctCd}
AND GI.GDS_SE_CD = #{gdsSeCd}
AND GI.SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
ORDER BY PCKG_YMD, PCKGNO, PCKG_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.insertPckgPrfmnc */
    		INTO TB_PCKG_PRFMNC (
				  APC_CD
				, PCKGNO
				, PCKG_SN
				, PCKG_YMD
				, PCKG_QNTT
				, PCKG_WGHT
				, WAREHOUSE_SE_CD
				, PCKG_CMNDNO
				, RMRK
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
				  #{apcCd}
				, #{pckgno}
				, #{pckgSn}
				, #{pckgYmd}
				, #{pckgQntt}
				, #{pckgWght}
				, #{warehouseSeCd}
				, #{pckgCmndno}
				, #{rmrk}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updatePckgPrfmnc */
			TB_PCKG_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, PCKG_YMD 			= #{pckgYmd}
			, PCKG_QNTT 		= #{pckgQntt}
			, PCKG_WGHT 		= #{pckgWght}
			, WAREHOUSE_SE_CD	= #{warehouseSeCd}
			, RMRK 				= #{rmrk}
			, PCKG_CMNDNO 		= #{pckgCmndno}


			, DEL_YN			= #{delYn}


		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}


    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updatePckgPrfmncForDelY */
			TB_PCKG_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, DEL_YN					= 'Y'
		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}

mapper.delete:

    	DELETE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.deletePckgPrfmnc */
		  FROM TB_PCKG_PRFMNC
		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updateInptPckgno */
			TB_PCKG_INPT_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}

			, PCKGNO = #{pckgno}

		 WHERE APC_CD = #{apcCd}
		   AND SORTNO = #{sortno}
		   AND GRD_CD = #{grdCd}
		   AND PCKGNO IS NULL
