mapper.insert:

    	INSERT /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.insertPckgPrfmnc */
    		INTO TB_PCKG_PRFMNC (
				  APC_CD
				, PCKGNO
				, PCKG_SN
				, PCKG_YMD
				, PCKG_QNTT
				, PCKG_WGHT
				, WAREHOUSE_SE_CD
				, PCKG_CMNDNO
				, RMRK
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
				  #{apcCd}
				, #{pckgno}
				, #{pckgSn}
				, #{pckgYmd}
				, #{pckgQntt}
				, #{pckgWght}
				, #{warehouseSeCd}
				, #{pckgCmndno}
				, #{rmrk}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
   			)

mapper.update:

    	UPDATE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updatePckgPrfmnc */
			TB_PCKG_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, PCKG_YMD 			= #{pckgYmd}
			, PCKG_QNTT 		= #{pckgQntt}
			, PCKG_WGHT 		= #{pckgWght}
			, WAREHOUSE_SE_CD	= #{warehouseSeCd}
			, RMRK 				= #{rmrk}
			, PCKG_CMNDNO 		= #{pckgCmndno}


			, DEL_YN			= #{delYn}


		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}

mapper.delete:

    	DELETE /* com.at.apcss.am.pckg.mapper.PckgPrfmncMapper.updatePckgPrfmnc */
		  FROM TB_PCKG_PRFMNC
		 WHERE APC_CD = #{apcCd}
		   AND PCKGNO = #{pckgno}
		   AND PCKG_SN = #{pckgSn}
