mapper.select:
SELECT /* com.at.apcss.am.sls.mapper.SlsPrfmncMapper.selectSlsPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM FROM ( SELECT SP.APC_CD AS APC_CD , SP.SLS_YMD AS SLS_YMD , SP.SLS_SN AS SLS_SN , SP.SLS_YMD || SP.SLS_SN AS SLSNO , SP.ITEM_CD AS ITEM_CD , SP.VRTY_CD AS VRTY_CD , SP.SPCFCT_CD AS SPCFCT_CD , SP.GDS_CD AS GDS_CD , SP.CNPT_CD AS CNPT_CD , SP.BRND_NM AS BRND_NM , SP.SPMT_YMD AS SPMT_YMD , SP.SLS_UNTPRC AS SLS_UNTPRC , SP.QNTT AS QNTT , SP.WGHT AS WGHT , SP.CFMTN_AMT AS CFMTN_AMT , SP.CFMTN_YN AS CFMTN_YN , SP.CLCTM_YN AS CLCTM_YN , SUM(SP.QNTT) OVER() AS TOT_QNTT , SUM(SP.WGHT) OVER() AS TOT_WGHT , SUM(SP.CFMTN_AMT) OVER() AS TOT_CFMTN_AMT FROM TB_SLS_PRFMNC SP WHERE SP.CFMTN_YN = 'Y' AND SP.DEL_YN = 'N'
AND SP.APC_CD = #{apcCd}
AND SP.SLS_YMD >= #{slsYmdFrom}
AND SP.SLS_YMD <= #{slsYmdTo}
AND (SELECT FN_GET_CNPT_NM(SP.CNPT_CD, SP.APC_CD) FROM DUAL) LIKE '%' || #{cnptNm} || '%'
AND SP.ITEM_CD = #{itemCd}
AND SP.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND SP.CFMTN_YN = #{cfmtnYn}
) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

    	INSERT INTO TB_SLS_PRFMNC	/* com.at.apcss.am.sls.mapper.SlsPrfmncMapper.insertSlsPrfmnc */
    		(
    			APC_CD
		   	  , SLS_YMD
		      , SLS_SN
    		  , CNPT_CD
    		  , GDS_CD
    		  , QNTT
    		  , WGHT
    		  , ITEM_CD
    		  , VRTY_CD
    		  , SPCFCT_CD
    		  , GRD_CD
    		  , BRND_NM
    		  , SPMT_YMD
    		  , SLS_UNTPRC
    		  , RKNG_AMT
    		  , CFMTN_AMT
    		  , CFMTN_YN
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
		 	    #{apcCd}
		   	  , #{slsYmd}
		   	  , #{slsSn}
    		  , #{cnptCd}
    		  , #{gdsCd}
    		  , #{qntt}
    		  , #{wght}
    		  , #{itemCd}
    		  , #{vrtyCd}
    		  , #{spcfctCd}
    		  , #{grdCd}
    		  , #{brndNm}
    		  , #{spmtYmd}
    		  , #{slsUntprc}
    		  , #{rkngAmt}
    		  , #{cfmtnAmt}
    		  , #{cfmtnYn}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_SLS_PRFMNC  /* com.at.apcss.am.sls.mapper.SlsPrfmncMapper.updateSlsPrfmnc */
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}


    		 , CFMTN_AMT 				= #{cfmtnAmt}


    		 , CFMTN_YN 				= #{cfmtnYn}


			 , CLCTM_YN					= #{clctmYn}


		 WHERE APC_CD 					= #{apcCd}
		   AND SLS_YMD 					= #{slsYmd}
		   AND SLS_SN 					= #{slsSn}

mapper.delete:

DELETE /* com.at.apcss.am.sls.mapper.SlsPrfmncMapper.deleteSlsPrfmnc */
  FROM TB_SLS_PRFMNC
 WHERE APC_CD 		= #{apcCd}
   AND SLS_YMD 		= #{slsYmd}
   AND SLS_SN 		= #{slsSn}


DELETE /* com.at.apcss.am.sls.mapper.SlsPrfmncMapper.deleteSlsPrfmncAll */
  FROM TB_SLS_PRFMNC
 WHERE APC_CD 		= #{apcCd}
   AND SPMTNO 		= #{spmtno}

mapper.select:
SELECT pt0.* , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM , (SELECT FN_GET_GRD_NM_GDS(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM FROM ( SELECT SP.APC_CD AS APC_CD , SP.SLS_YMD AS SLS_YMD , SP.SLS_SN AS SLS_SN , SP.CNPT_CD AS CNPT_CD , SP.GDS_CD AS GDS_CD , SP.ITEM_CD AS ITEM_CD , SP.VRTY_CD AS VRTY_CD , SP.SPCFCT_CD AS SPCFCT_CD , SP.GRD_CD AS GRD_CD , SP.BRND_NM AS BRND_NM , SP.SPMT_YMD AS SPMT_YMD , SP.QNTT AS QNTT , SP.WGHT AS WGHT , SP.SLS_UNTPRC AS SLS_UNTPRC , SP.RKNG_AMT AS RKNG_AMT , SP.CFMTN_AMT AS CFMTN_AMT , SP.CFMTN_YN AS CFMTN_YN , SP.DEL_YN AS DEL_YN , CASE WHEN (SELECT COUNT(*) FROM TB_SPMT_PRFMNC_DTL X WHERE X.APC_CD = SP.APC_CD AND X.SPMTNO = SP.SPMTNO AND X.PCKGNO = SP.PCKGNO AND X.PCKG_SN = SP.PCKG_SN) = 1 THEN 'Y' ELSE 'N' END AS CFMTN_PSBLTY_YN FROM TB_SLS_PRFMNC SP WHERE SP.DEL_YN = 'N'
AND SP.APC_CD = #{apcCd}
AND SP.SLS_YMD >= #{slsYmdFrom}
AND SP.SLS_YMD <= #{slsYmdTo}
AND SP.CNPT_CD = #{cnptCd}
AND SP.ITEM_CD = #{itemCd}
AND SP.VRTY_CD = #{vrtyCd}
AND SP.CFMTN_YN = #{cfmtnYn}
AND (SELECT FN_GET_CNPT_NM(SP.CNPT_CD, SP.APC_CD) FROM DUAL) LIKE '%' || #{cnptNm} || '%'
) pt0
