mapper.select:
SELECT /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.selectSortCmndList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM
FROM ( SELECT SC.APC_CD AS APC_CD -- APC코드 , SC.SORT_CMNDNO AS SORT_CMNDNO -- 선별지시번호 , SC.SORT_CMND_SN AS SORT_CMND_SN -- 선별지시순번 , SC.SORT_CMND_YMD AS SORT_CMND_YMD -- 선별지시일자 , SC.FCLT_CD AS FCLT_CD -- 설비코드 , SC.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD -- 창고구분코드 , SC.PLTNO AS PLTNO -- 팔레트번호 , SC.PRDCR_CD AS PRDCR_CD -- 생산자코드 , SC.ITEM_CD AS ITEM_CD -- 품목코드 , SC.VRTY_CD AS VRTY_CD -- 품종코드 , SC.CMND_QNTT AS CMND_QNTT -- 지시수량 , SC.CMND_WGHT AS CMND_WGHT -- 지시중량 , SC.RMRK AS RMRK -- 비고 , SC.DEL_YN AS DEL_YN -- 삭제유무 FROM TB_SORT_CMND SC WHERE SC.DEL_YN = 'N' AND SC.APC_CD = #{apcCd}
AND SC.SORT_CMND_YMD = #{sortCmndYmd}
AND SC.SORT_CMND_YMD >= #{sortCmndFromYmd}
AND SC.SORT_CMND_YMD <= #{sortCmndToYmd}
AND SC.FCLT_CD = #{fcltCd}
AND SC.ITEM_CD = #{itemCd}
AND SC.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND SC.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND SC.PRDCR_CD = #{prdcrCd}
ORDER BY SORT_CMND_YMD, SORT_CMNDNO, SORT_CMND_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

mapper.insert:

INSERT INTO TB_SORT_CMND
	(
		APC_CD
	  , SORT_CMNDNO
	  , SORT_CMND_SN
	  , SORT_CMND_YMD
	  , FCLT_CD
	  , WAREHOUSE_SE_CD
	  , PLTNO
	  , PRDCR_CD
	  , ITEM_CD
	  , VRTY_CD
	  , CMND_QNTT
	  , CMND_WGHT
	  , RMRK
	  , SYS_FRST_INPT_DT
	  , SYS_FRST_INPT_USER_ID
	  , SYS_FRST_INPT_PRGRM_ID
	  , SYS_LAST_CHG_DT
	  , SYS_LAST_CHG_USER_ID
	  , SYS_LAST_CHG_PRGRM_ID
	)
VALUES
	(
	   #{apcCd}
	 , #{sortCmndno}
	 , #{sortCmndSn}
	 , #{sortCmndYmd}
	 , #{fcltCd}
	 , #{warehouseSeCd}
	 , #{pltno}
	 , #{prdcrCd}
	 , #{itemCd}
	 , #{vrtyCd}
	 , #{cmndQntt}
	 , #{cmndWght}
	 , #{rmrk}
	 , SYSDATE
	 , #{sysFrstInptUserId}
	 , #{sysFrstInptPrgrmId}
	 , SYSDATE
	 , #{sysLastChgUserId}
	 , #{sysLastChgPrgrmId}
	)

mapper.update:

UPDATE /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.deleteSortCmnd */
  	   TB_SORT_CMND
   SET SYS_LAST_CHG_DT 			= SYSDATE
	 , SYS_LAST_CHG_USER_ID 	= #{sysLastChgUserId}
	 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
	 , DEL_YN					= 'Y'
 WHERE APC_CD			= #{apcCd}
   AND SORT_CMNDNO		= #{sortCmndno}

mapper.select:
SELECT pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM FROM (
SELECT /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.selectPckgCmndTrgetList */ SI.APC_CD AS APC_CD , SI.SORTNO AS SORTNO , SI.GRD_CD AS GRD_CD , SI.INPT_YMD AS INPT_YMD , SI.FCLT_CD AS FCLT_CD , SI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , SI.ITEM_CD AS ITEM_CD , SI.VRTY_CD AS VRTY_CD , SI.SPCFCT_CD AS SPCFCT_CD , SI.GDS_SE_CD AS GDS_SE_CD , SI.WRHS_SE_CD AS WRHS_SE_CD , SI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , SI.SORT_QNTT AS SORT_QNTT , SI.SORT_WGHT AS SORT_WGHT , SI.PCKG_QNTT AS PCKG_QNTT , SI.PCKG_WGHT AS PCKG_WGHT , SI.INVNTR_QNTT AS INVNTR_QNTT , SI.INVNTR_WGHT AS INVNTR_WGHT , SI.DEL_YN AS DEL_YN , SI.PRDCTN_YR AS PRDCTN_YR , SI.SORT_SN AS SORT_SN , SI.RMRK AS RMRK
FROM TB_SORT_INVNTR SI WHERE SI.APC_CD = #{apcCd} AND SI.INVNTR_WGHT > 0 AND SI.DEL_YN = 'N'
AND SI.INPT_YMD >= #{sortCmndFromYmd}
AND SI.INPT_YMD <= #{sortCmndToYmd}
AND SI.ITEM_CD = #{itemCd}
AND SI.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND SI.RPRS_PRDCR_CD = #{prdcrCd}
) pt0
