mapper.insert:

    	INSERT /* com.at.apcss.am.sort.mapper.SortInptPrfmncMapper.insertSortInptPrfmnc */
			INTO TB_SORT_INPT_PRFMNC (
				  APC_CD
				, WRHSNO
				, INPT_SN
				, INPT_YMD
				, QNTT
				, WGHT
				, SORTNO
				, SORT_CMNDNO
				, RMRK
	    		, SYS_FRST_INPT_DT
	    		, SYS_FRST_INPT_USER_ID
	    		, SYS_FRST_INPT_PRGRM_ID
	    		, SYS_LAST_CHG_DT
	    		, SYS_LAST_CHG_USER_ID
	    		, SYS_LAST_CHG_PRGRM_ID
			) VALUES (
				  #{apcCd}
				, #{wrhsno}
				, NVL((SELECT MAX(X.INPT_SN) FROM TB_SORT_INPT_PRFMNC X WHERE X.APC_CD = #{apcCd} AND X.WRHSNO = #{wrhsno}), 0) + 1
				, #{inptYmd}
				, #{qntt}
				, #{wght}
				, #{sortno}
				, #{sortCmndno}
				, #{rmrk}
	    		, SYSDATE
	    		, #{sysFrstInptUserId}
	    		, #{sysFrstInptPrgrmId}
	    		, SYSDATE
	    		, #{sysLastChgUserId}
	    		, #{sysLastChgPrgrmId}
			)

mapper.update:

    	UPDATE /* com.at.apcss.am.sort.mapper.SortInptPrfmncMapper.updateSortInptPrfmnc */
			TB_SORT_INPT_PRFMNC
		   SET
		   	  SYS_LAST_CHG_DT 			= SYSDATE
    		, SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		, SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			, INPT_YMD 		= #{inptYmd}
			, QNTT 			= #{qntt}
			, WGHT 			= #{wght}
			, RMRK 			= #{rmrk}
			, SORTNO		= #{sortno}
			, SORT_CMNDNO 	= #{sortCmndno}


			, DEL_YN			= #{delYn}


		 WHERE APC_CD = #{apcCd}
		   AND WRHSNO = #{wrhsno}

mapper.delete:

    	DELETE /* com.at.apcss.am.sort.mapper.SortInptPrfmncMapper.deleteSortInptPrfmnc */
			TB_SORT_INPT_PRFMNC
		 WHERE APC_CD = #{apcCd}
		   AND WRHSNO = #{wrhsno}
