SELECT /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.selectSortPrfmncList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS RPRS_PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD) FROM DUAL) AS GRD_NM , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM FROM ( SELECT SP.APC_CD AS APC_CD , SP.SORTNO AS SORTNO , SP.SORT_SN AS SORT_SN , SP.GRD_CD AS GRD_CD , SP.INPT_YMD AS INPT_YMD , SP.FCLT_CD AS FCLT_CD , SP.SORT_QNTT AS SORT_QNTT , SP.SORT_WGHT AS SORT_WGHT , SP.RMRK AS RMRK , SI.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , SI.ITEM_CD AS ITEM_CD , SI.VRTY_CD AS VRTY_CD , SI.SPCFCT_CD AS SPCFCT_CD , SI.GDS_SE_CD AS GDS_SE_CD , SI.WRHS_SE_CD AS WRHS_SE_CD , SI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , (SELECT SUM(X.QNTT) FROM TB_SORT_INPT_PRFMNC X WHERE X.APC_CD = SP.APC_CD AND X.SORTNO = SP.SORTNO AND X.DEL_YN = 'N') AS INPT_QNTT , (SELECT SUM(X.WGHT) FROM TB_SORT_INPT_PRFMNC X WHERE X.APC_CD = SP.APC_CD AND X.SORTNO = SP.SORTNO AND X.DEL_YN = 'N') AS INPT_WGHT , (SELECT SUM(X.WGHT) FROM TB_SORT_INPT_PRFMNC X WHERE X.APC_CD = SP.APC_CD AND X.SORTNO = SP.SORTNO AND X.DEL_YN = 'N') - SI.SORT_WGHT AS LS , (SELECT MAX(X.WRHSNO) FROM TB_SORT_INPT_PRFMNC X WHERE X.APC_CD = SP.APC_CD AND X.SORTNO = SP.SORTNO AND X.DEL_YN = 'N') AS WRHSNO
FROM TB_SORT_PRFMNC SP , TB_SORT_INVNTR SI WHERE SP.APC_CD = SI.APC_CD AND SP.SORTNO = SI.SORTNO AND SP.SORT_SN = SI.SORT_SN AND SP.DEL_YN = 'N' AND SI.DEL_YN = 'N' AND SP.APC_CD = #{apcCd}
AND SP.INPT_YMD >= #{inptYmdFrom}
AND SP.INPT_YMD <= #{inptYmdTo}
AND SI.ITEM_CD = #{itemCd}
AND SI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND SI.SPCFCT_CD = #{spcfctCd}
AND SI.RPRS_PRDCR_CD = #{rprsPrdcrCd}
AND SI.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
AND SP.FCLT_CD = #{fcltCd}
ORDER BY APC_CD, INPT_YMD, SORTNO, SORT_SN ) pt0
OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY

SELECT /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.selectSortPrfmncGrdList */
ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT ,
pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.RPRS_PRDCR_CD, pt0.APC_CD) FROM DUAL) AS RPRS_PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_1) FROM DUAL) AS GRD_NM_1 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_2) FROM DUAL) AS GRD_NM_2 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_3) FROM DUAL) AS GRD_NM_3 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_4) FROM DUAL) AS GRD_NM_4 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_5) FROM DUAL) AS GRD_NM_5 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_6) FROM DUAL) AS GRD_NM_6 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_7) FROM DUAL) AS GRD_NM_7 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_8) FROM DUAL) AS GRD_NM_8 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_9) FROM DUAL) AS GRD_NM_9 , (SELECT FN_GET_GRD_NM_SORT(pt0.APC_CD, pt0.ITEM_CD, pt0.GRD_CD_10) FROM DUAL) AS GRD_NM_10 , pt0.GRD_CD_11 AS GRD_NM_11 , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', pt0.FCLT_CD, pt0.APC_CD) FROM DUAL) AS FCLT_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_SE_CD', pt0.GDS_SE_CD) FROM DUAL) AS GDS_SE_NM , (SELECT FN_GET_COM_CD_VL_NM('WRHS_SE_CD', pt0.WRHS_SE_CD) FROM DUAL) AS WRHS_SE_NM FROM ( SELECT X.APC_CD AS APC_CD , X.SORTNO AS SORTNO , MAX(X.INPT_YMD) AS INPT_YMD , MAX(X.FCLT_CD) AS FCLT_CD , MAX(X.RMRK) AS RMRK , MAX(X.RPRS_PRDCR_CD) AS RPRS_PRDCR_CD , MAX(X.ITEM_CD) AS ITEM_CD , MAX(X.VRTY_CD) AS VRTY_CD , MAX(X.SPCFCT_CD) AS SPCFCT_CD , MAX(X.GDS_SE_CD) AS GDS_SE_CD , MAX(X.WRHS_SE_CD) AS WRHS_SE_CD , MAX(X.WAREHOUSE_SE_CD) AS WAREHOUSE_SE_CD , MAX(CASE WHEN GRD_RANK = 1 THEN X.GRD_CD END) AS GRD_CD_1 , SUM(CASE WHEN GRD_RANK = 1 THEN SORT_QNTT END) AS SORT_QNTT_1 , SUM(CASE WHEN GRD_RANK = 1 THEN SORT_WGHT END) AS SORT_WGHT_1 , MAX(CASE WHEN GRD_RANK = 2 THEN X.GRD_CD END) AS GRD_CD_2 , SUM(CASE WHEN GRD_RANK = 2 THEN SORT_QNTT END) AS SORT_QNTT_2 , SUM(CASE WHEN GRD_RANK = 2 THEN SORT_WGHT END) AS SORT_WGHT_2 , MAX(CASE WHEN GRD_RANK = 3 THEN X.GRD_CD END) AS GRD_CD_3 , SUM(CASE WHEN GRD_RANK = 3 THEN SORT_QNTT END) AS SORT_QNTT_3 , SUM(CASE WHEN GRD_RANK = 3 THEN SORT_WGHT END) AS SORT_WGHT_3 , MAX(CASE WHEN GRD_RANK = 4 THEN X.GRD_CD END) AS GRD_CD_4 , SUM(CASE WHEN GRD_RANK = 4 THEN SORT_QNTT END) AS SORT_QNTT_4 , SUM(CASE WHEN GRD_RANK = 4 THEN SORT_WGHT END) AS SORT_WGHT_4 , MAX(CASE WHEN GRD_RANK = 5 THEN X.GRD_CD END) AS GRD_CD_5 , SUM(CASE WHEN GRD_RANK = 5 THEN SORT_QNTT END) AS SORT_QNTT_5 , SUM(CASE WHEN GRD_RANK = 5 THEN SORT_WGHT END) AS SORT_WGHT_5 , MAX(CASE WHEN GRD_RANK = 6 THEN X.GRD_CD END) AS GRD_CD_6 , SUM(CASE WHEN GRD_RANK = 6 THEN SORT_QNTT END) AS SORT_QNTT_6 , SUM(CASE WHEN GRD_RANK = 6 THEN SORT_WGHT END) AS SORT_WGHT_6 , MAX(CASE WHEN GRD_RANK = 7 THEN X.GRD_CD END) AS GRD_CD_7 , SUM(CASE WHEN GRD_RANK = 7 THEN SORT_QNTT END) AS SORT_QNTT_7 , SUM(CASE WHEN GRD_RANK = 7 THEN SORT_WGHT END) AS SORT_WGHT_7 , MAX(CASE WHEN GRD_RANK = 8 THEN X.GRD_CD END) AS GRD_CD_8 , SUM(CASE WHEN GRD_RANK = 8 THEN SORT_QNTT END) AS SORT_QNTT_8 , SUM(CASE WHEN GRD_RANK = 8 THEN SORT_WGHT END) AS SORT_WGHT_8 , MAX(CASE WHEN GRD_RANK = 9 THEN X.GRD_CD END) AS GRD_CD_9 , SUM(CASE WHEN GRD_RANK = 9 THEN SORT_QNTT END) AS SORT_QNTT_9 , SUM(CASE WHEN GRD_RANK = 9 THEN SORT_WGHT END) AS SORT_WGHT_9 , MAX(CASE WHEN GRD_RANK = 10 THEN X.GRD_CD END) AS GRD_CD_10 , SUM(CASE WHEN GRD_RANK = 10 THEN SORT_QNTT END) AS SORT_QNTT_10 , SUM(CASE WHEN GRD_RANK = 10 THEN SORT_WGHT END) AS SORT_WGHT_10 , MAX(CASE WHEN GRD_RANK > 10 THEN '그외' END) AS GRD_CD_11 , SUM(CASE WHEN GRD_RANK > 10 TH