FROM ( SELECT B.ITEM_CD , A.INPT_YMD , B.RPRS_PRDCR_CD , FN_GET_PRDCR_NM (B.RPRS_PRDCR_CD, A.APC_CD) AS PRDCR_NM , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('01') THEN A.SORT_QNTT END AS R_2XL , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('02') THEN A.SORT_QNTT END AS R_XL , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('03') THEN A.SORT_QNTT END AS R_L , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('04') THEN A.SORT_QNTT END AS R_M , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('05') THEN A.SORT_QNTT END AS R_S , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('06') THEN A.SORT_QNTT END AS R_SS , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('01') THEN A.SORT_QNTT END AS Y_2XL , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('02') THEN A.SORT_QNTT END AS Y_XL , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('03') THEN A.SORT_QNTT END AS Y_L , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('04') THEN A.SORT_QNTT END AS Y_M , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('05') THEN A.SORT_QNTT END AS Y_S , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('06') THEN A.SORT_QNTT END AS Y_SS , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('01') THEN A.SORT_QNTT END AS O_2XL , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('02') THEN A.SORT_QNTT END AS O_XL , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('03') THEN A.SORT_QNTT END AS O_L , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('04') THEN A.SORT_QNTT END AS O_M , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('05') THEN A.SORT_QNTT END AS O_S , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('06') THEN A.SORT_QNTT END AS O_SS , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('01','02','03','04','05','06') THEN A.SORT_QNTT END AS R_TOT , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('01','02','03','04','05','06') THEN A.SORT_QNTT END AS Y_TOT , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('01','02','03','04','05','06') THEN A.SORT_QNTT END AS O_TOT , A.SORT_QNTT AS SORT_QNTT FROM USRAM.TB_SORT_PRFMNC A , USRAM.TB_SORT_INVNTR B WHERE A.APC_CD = B.APC_CD AND A.SORTNO = B.SORTNO AND A.SORT_SN = B.SORT_SN AND A.GRD_CD IN ('01','02','03','04','05','06') AND A.DEL_YN = 'N' AND B.DEL_YN = 'N'
AND A.APC_CD = #{apcCd}
AND B.ITEM_CD = #{itemCd}
AND A.INPT_YMD >= #{inptYmdFrom}
AND A.INPT_YMD <= #{inptYmdTo}
)
GROUP BY INPT_YMD, PRDCR_NM
UNION ALL
