SELECT INPT_YMD , 'C품' AS GRD , PRDCR_NM , SUM(R_2XL) AS red2Xl , SUM(R_XL) AS redXl , SUM(R_L) AS redL , SUM(R_M) AS redM , SUM(R_S) AS redS , SUM(R_SS) AS redSs , SUM(Y_2XL) AS ylw2Xl , SUM(Y_XL) AS ylwXl , SUM(Y_L) AS ylwL , SUM(Y_M) AS ylwM , SUM(Y_S) AS ylwS , SUM(Y_SS) AS ylwSs , SUM(O_2XL) AS org2Xl , SUM(O_XL) AS orgXl , SUM(O_L) AS orgL , SUM(O_M) AS orgM , SUM(O_S) AS orgS , SUM(O_SS) AS orgSs , SUM(SORT_QNTT) AS tot , SUM(R_TOT) redTot , SUM(Y_TOT) ylwTot , SUM(O_TOT) orgTot
FROM ( SELECT B.ITEM_CD , A.INPT_YMD , B.RPRS_PRDCR_CD , FN_GET_PRDCR_NM (B.RPRS_PRDCR_CD, A.APC_CD) AS PRDCR_NM , 0 AS R_2XL , 0 AS R_XL , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('13') THEN A.SORT_QNTT END AS R_L , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('14') THEN A.SORT_QNTT END AS R_M , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('15') THEN A.SORT_QNTT END AS R_S , 0 AS R_SS , 0 AS Y_2XL , 0 AS Y_XL , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('13') THEN A.SORT_QNTT END AS Y_L , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('14') THEN A.SORT_QNTT END AS Y_M , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('15') THEN A.SORT_QNTT END AS Y_S , 0 AS Y_SS , 0 AS O_2XL , 0 AS O_XL , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('13') THEN A.SORT_QNTT END AS O_L , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('14') THEN A.SORT_QNTT END AS O_M , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('15') THEN A.SORT_QNTT END AS O_S , 0 AS O_SS , CASE WHEN B.VRTY_CD = '0300' AND A.GRD_CD IN ('13','14','15') THEN A.SORT_QNTT END AS R_TOT , CASE WHEN B.VRTY_CD = '0200' AND A.GRD_CD IN ('13','14','15') THEN A.SORT_QNTT END AS Y_TOT , CASE WHEN B.VRTY_CD = '0400' AND A.GRD_CD IN ('13','14','15') THEN A.SORT_QNTT END AS O_TOT , A.SORT_QNTT AS SORT_QNTT FROM USRAM.TB_SORT_PRFMNC A , USRAM.TB_SORT_INVNTR B
WHERE A.APC_CD = B.APC_CD AND A.SORTNO = B.SORTNO AND A.SORT_SN = B.SORT_SN AND A.GRD_CD IN ('13','14','15') AND A.DEL_YN = 'N' AND B.DEL_YN = 'N'
AND A.APC_CD = #{apcCd}
AND B.ITEM_CD = #{itemCd}
AND A.INPT_YMD >= #{inptYmdFrom}
AND A.INPT_YMD <= #{inptYmdTo}
) GROUP BY INPT_YMD, PRDCR_NM

SELECT MAX(ITEM_NM) AS ITEM_NM , MAX(VRTY_NM) AS VRTY_NM , INPT_YMD , PRDCR_NM , MAX(GRD_NM) AS GRD_NM , SUM(SORT_QNTT1) AS SORT_QNTT1 , SUM(SORT_QNTT2) AS SORT_QNTT2 , SUM(SORT_QNTT3) AS SORT_QNTT3 , SUM(SORT_QNTT4) AS SORT_QNTT4 , SUM(SORT_QNTT5) AS SORT_QNTT5 , SUM(SORT_WGHT1) AS SORT_WGHT1 , SUM(SORT_WGHT2) AS SORT_WGHT2 , SUM(SORT_WGHT3) AS SORT_WGHT3 , SUM(SORT_WGHT4) AS SORT_WGHT4 , SUM(SORT_WGHT5) AS SORT_WGHT5 , SUM(SORT_QNTT) AS SORT_QNTT , SUM(SORT_WGHT) AS SORT_QNTT
FROM (
SELECT A.GRD_CD , (SELECT FN_GET_GRD_NM_SORT(A.APC_CD, B.ITEM_CD, A.GRD_CD) FROM DUAL) AS GRD_NM , B.ITEM_CD , A.INPT_YMD , CASE WHEN C.RNK = 1 THEN A.SORT_QNTT ELSE 0 END AS SORT_QNTT1 , CASE WHEN C.RNK = 2 THEN A.SORT_QNTT ELSE 0 END AS SORT_QNTT2 , CASE WHEN C.RNK = 3 THEN A.SORT_QNTT ELSE 0 END AS SORT_QNTT3 , CASE WHEN C.RNK = 4 THEN A.SORT_QNTT ELSE 0 END AS SORT_QNTT4 , CASE WHEN C.RNK = 5 THEN A.SORT_QNTT ELSE 0 END AS SORT_QNTT5 , CASE WHEN RNK = 1 THEN A.SORT_WGHT ELSE 0 END AS SORT_WGHT1 , CASE WHEN RNK = 2 THEN A.SORT_WGHT ELSE 0 END AS SORT_WGHT2 , CASE WHEN RNK = 3 THEN A.SORT_WGHT ELSE 0 END AS SORT_WGHT3 , CASE WHEN RNK = 4 THEN A.SORT_WGHT ELSE 0 END AS SORT_WGHT4 , CASE WHEN RNK = 5 THEN A.SORT_WGHT ELSE 0 END AS SORT_WGHT5 , A.SORT_QNTT , A.SORT_WGHT , B.RPRS_PRDCR_CD AS RPRS_PRDCR_CD , FN_GET_PRDCR_NM (B.RPRS_PRDCR_CD, A.APC_CD) AS PRDCR_NM , B.VRTY_CD AS VRTY_CD , (SELECT FN_GET_ITEM_NM(B.ITEM_CD, B.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(B.ITEM_CD, B.VRTY_CD, B.APC_CD) FROM DUAL) AS VRTY_NM
FROM USRAM.TB_SORT_PRFMNC A , USRAM.TB_SORT_INVNTR B , ( SELECT ITEM_CD , VRTY_CD , GRD_CD , RANK() OVER(PARTITION BY VRTY_CD ORDER BY GRD_CD) AS RNK FROM (SELECT ITEM_CD,VRTY_CD,GRD_CD FROM TB_SORT_INVNTR WHERE 1=1
AND APC_CD = #{apcCd}
AND ITEM_CD = #{itemCd}
AND VRTY_CD = #{vrtyCd}
GROUP BY ITEM_CD,VRTY_CD,GRD_CD) ) C
WHERE A.APC_CD = B.APC_CD AND A.SORTNO = B.SORTNO AND A.SORT_SN = B.SORT_SN AND A.GRD_CD = C.GRD_CD AND A.DEL_YN = 'N' AND B.DEL_YN = 'N'
AND A.APC_CD = #{apcCd}
AND B.ITEM_CD = #{itemCd}
AND B.VRTY_CD = #{vrtyCd}
AND A.INPT_YMD >= #{inptYmdFrom}
AND A.INPT_YMD <= #{inptYmdTo}
AND B.RPRS_PRDCR_CD = #{rprsPrdcrCd}
) X GROUP BY INPT_YMD, PRDCR_NM

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM (
SELECT pt0.* , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_COM_CD_VL_NM('WAREHOUSE_SE_CD', pt0.WAREHOUSE_SE_CD, pt0.APC_CD) FROM DUAL) AS WAREHOUSE_SE_NM FROM (
SELECT /* com.at.apcss.am.sort.mapper.SortPrfmncMapper.selectRawMtrInvntrList */ RI.APC_CD AS APC_CD , RI.PLTNO AS PLTNO , RI.WRHS_YMD AS WRHS_YMD , RI.PRDCR_CD AS PRDCR_CD , RI.WAREHOUSE_SE_CD AS WAREHOUSE_SE_CD , RI.ITEM_CD AS ITEM_CD , RI.VRTY_CD AS VRTY_CD , SC.SORT_CMNDNO AS SORT_CMNDNO , SUM(RI.INVNTR_QNTT) AS INVNTR_QNTT , SUM(RI.INVNTR_WGHT) AS INVNTR_WGHT , SUM(RI.INPT_QNTT) AS INPT_QNTT , SUM(RI.INPT_WGHT) AS INPT_WGHT , SUM(SC.CMND_QNTT) AS CMND_QNTT , SUM(SC.CMND_WGHT) AS CMND_WGHT
FROM TB_RAW_MTR_INVNTR RI
LEFT JOIN TB_SORT_CMND SC ON SC.APC_CD = RI.APC_CD AND SC.WAREHOUSE_SE_CD = RI.WAREHOUSE_SE_CD AND SC.PLTNO = RI.PLTNO AND SC.PRDCR_CD = RI.PRDCR_CD AND SC.ITEM_CD = RI.ITEM_CD AND SC.VRTY_CD = RI.VRTY_CD AND SC.DEL_YN = 'N'
WHERE RI.APC_CD = #{apcCd} AND RI.DEL_YN = 'N'
AND RI.PRDCR_CD = #{prdcrCd}
AND RI.ITEM_CD = #{itemCd}
AND RI.VRTY_CD = #{vrtyCd}
AND RI.WRHS_YMD = #{wrhsYmd}
AND RI.WRHS_YMD >= #{wrhsYmdFrom}
AND RI.WRHS_YMD <= #{wrhsYmdTo}
GROUP BY RI.APC_CD , RI.PLTNO , RI.WRHS_YMD , RI.PRDCR_CD , RI.WAREHOUSE_SE_CD , RI.ITEM_CD , RI.VRTY_CD , SC.SORT_CMNDNO ORDER BY APC_CD, WRHS_YMD, PLTNO, SORT_CMNDNO
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

