)

SELECT 'C품' AS GRD , SUM(R_2XL) AS red2Xl , SUM(R_XL) AS redXl , SUM(R_L) AS redL , SUM(R_M) AS redM , SUM(R_S) AS redS , SUM(R_SS) AS redSs , SUM(Y_2XL) AS ylw2Xl , SUM(Y_XL) AS ylwXl , SUM(Y_L) AS ylwL , SUM(Y_M) AS ylwM , SUM(Y_S) AS ylwS , SUM(Y_SS) AS ylwSs , SUM(O_2XL) AS org2Xl , SUM(O_XL) AS orgXl , SUM(O_L) AS orgL , SUM(O_M) AS orgM , SUM(O_S) AS orgS , SUM(O_SS) AS orgSs , SUM(INVNTR_QNTT) AS tot , SUM(R_TOT) AS redTot , SUM(Y_TOT) AS ylwTot , SUM(O_TOT) AS orgTot FROM ( SELECT SI.ITEM_CD
, SI.RPRS_PRDCR_CD , FN_GET_PRDCR_NM (SI.RPRS_PRDCR_CD, SI.APC_CD) AS PRDCR_NM , CASE WHEN SI.VRTY_CD = '0300' AND SI.GRD_CD IN ('13') THEN SI.INVNTR_QNTT END AS R_2XL , CASE WHEN SI.VRTY_CD = '0300' AND SI.GRD_CD IN ('14') THEN SI.INVNTR_QNTT END AS R_XL , CASE WHEN SI.VRTY_CD = '0300' AND SI.GRD_CD IN ('15') THEN SI.INVNTR_QNTT END AS R_L , 0 AS R_M , 0 AS R_S , 0 AS R_SS , CASE WHEN SI.VRTY_CD = '0200' AND SI.GRD_CD IN ('13') THEN SI.INVNTR_QNTT END AS Y_2XL , CASE WHEN SI.VRTY_CD = '0200' AND SI.GRD_CD IN ('14') THEN SI.INVNTR_QNTT END AS Y_XL , CASE WHEN SI.VRTY_CD = '0200' AND SI.GRD_CD IN ('15') THEN SI.INVNTR_QNTT END AS Y_L , 0 AS Y_M , 0 AS Y_S , 0 AS Y_SS , CASE WHEN SI.VRTY_CD = '0400' AND SI.GRD_CD IN ('13') THEN SI.INVNTR_QNTT END AS O_2XL , CASE WHEN SI.VRTY_CD = '0400' AND SI.GRD_CD IN ('14') THEN SI.INVNTR_QNTT END AS O_XL , CASE WHEN SI.VRTY_CD = '0400' AND SI.GRD_CD IN ('15') THEN SI.INVNTR_QNTT END AS O_L , 0 AS O_M , 0 AS O_S , 0 AS O_SS , CASE WHEN SI.VRTY_CD = '0300' AND SI.GRD_CD IN ('13','14','15') THEN SI.INVNTR_QNTT END AS R_TOT , CASE WHEN SI.VRTY_CD = '0200' AND SI.GRD_CD IN ('13','14','15') THEN SI.INVNTR_QNTT END AS Y_TOT , CASE WHEN SI.VRTY_CD = '0400' AND SI.GRD_CD IN ('13','14','15') THEN SI.INVNTR_QNTT END AS O_TOT , SI.INVNTR_QNTT AS INVNTR_QNTT FROM USRAM.TB_SORT_INVNTR SI WHERE SI.GRD_CD IN ('13','14','15') AND SI.DEL_YN = 'N'
AND SI.APC_CD = #{apcCd}
AND SI.ITEM_CD = #{itemCd}
AND SI.INPT_YMD >= #{inptYmdFrom}
AND SI.INPT_YMD <= #{inptYmdTo}
)

mapper.select:
SELECT BP.APC_CD AS APC_CD , BP.WRHS_YMD AS WRHS_YMD , BP.BFFA_WRHSNO AS BFFA_WRHSNO , BP.PLTNO AS PLTNO , BP.PRDCR_CD AS PRDCR_CD , (SELECT FN_GET_PRDCR_NM(BP.PRDCR_CD, BP.APC_CD) FROM DUAL)AS PRDCR_NM , BP.ITEM_CD AS ITEM_CD , BP.VRTY_CD AS VRTY_CD , BP.FCLT_CD AS FCLT_CD , (SELECT FN_GET_COM_CD_VL_NM('SORT_FCLT_CD', BP.FCLT_CD, BP.APC_CD) FROM DUAL) AS FCLT_NM , BP.WRHS_QNTT AS WRHS_QNTT , BP.WRHS_WGHT AS WRHS_WGHT , NVL(BP.WRHS_WGHT, 0) + NVL(BP.GRD_TYPE_1_WGHT, 0) + NVL(BP.GRD_TYPE_2_WGHT, 0) + NVL(BP.GRD_TYPE_3_WGHT, 0) + NVL(BP.GRD_TYPE_4_WGHT, 0) + NVL(BP.GRD_TYPE_5_WGHT, 0)AS WHOL_WGHT , NVL(BP.GRD_TYPE_1_WGHT, 0) + NVL(BP.GRD_TYPE_2_WGHT, 0) + NVL(BP.GRD_TYPE_3_WGHT, 0) + NVL(BP.GRD_TYPE_4_WGHT, 0) + NVL(BP.GRD_TYPE_5_WGHT, 0)AS ICPT_WGHT , BP.RMRK AS RMRK , NVL(BP.GRD_TYPE_1_WGHT, 0) AS GRD_TYPE_1_WGHT , NVL(BP.GRD_TYPE_2_WGHT, 0) AS GRD_TYPE_2_WGHT , NVL(BP.GRD_TYPE_3_WGHT, 0) AS GRD_TYPE_3_WGHT , NVL(BP.GRD_TYPE_4_WGHT, 0) AS GRD_TYPE_4_WGHT , NVL(BP.GRD_TYPE_5_WGHT, 0) AS GRD_TYPE_5_WGHT FROM TB_BFFA_WRHS_PRFMNC BP WHERE 1=1
AND BP.APC_CD = ${apcCd}
AND BP.WRHS_YMD >= ${inptYmdFrom}
AND BP.WRHS_YMD <= ${inptYmdTo}
AND BP.FCLT_CD = #{fcltCd}
AND BP.PRDCR_CD = #{prdcrCd}
AND BP.ITEM_CD = #{itemCd}
ORDER BY BFFA_WRHSNO
