mapper.insert:
MERGE INTO TB_SHPGOT_APC_CRTR SAC USING (
SELECT #{item.apcCd} AS APC_CD , #{item.shpgotCrtrType} AS SHPGOT_CRTR_TYPE , #{item.crtrCd} AS CRTR_CD , #{item.crtrVl} AS CRTR_VL , #{item.crtrIndctNm} AS CRTR_INDCT_NM , #{item.indctSeq} AS INDCT_SEQ , #{item.userYn} AS USER_YN , #{item.rmrk} AS RMRK , #{item.sysLastChgUserId} AS SYS_LAST_CHG_USER_ID , #{item.sysLastChgPrgrmId} AS SYS_LAST_CHG_PRGRM_ID , #{item.sysFrstInptUserId} AS SYS_FRST_INPT_USER_ID , #{item.sysFrstInptPrgrmId} AS SYS_FRST_INPT_PRGRM_ID FROM DUAL
) source ON ( SAC.APC_CD = source.APC_CD AND SAC.SHPGOT_CRTR_TYPE = source.SHPGOT_CRTR_TYPE AND SAC.CRTR_CD = source.CRTR_CD ) WHEN MATCHED THEN UPDATE SET CRTR_VL = source.CRTR_VL ,CRTR_INDCT_NM = source.CRTR_INDCT_NM ,INDCT_SEQ = source.INDCT_SEQ ,USER_YN = source.USER_YN ,RMRK = source.RMRK ,SYS_LAST_CHG_DT = SYSDATE ,SYS_LAST_CHG_USER_ID = source.SYS_LAST_CHG_USER_ID ,SYS_LAST_CHG_PRGRM_ID = source.SYS_LAST_CHG_PRGRM_ID WHEN NOT MATCHED THEN INSERT ( APC_CD ,SHPGOT_CRTR_TYPE ,CRTR_CD ,CRTR_VL ,CRTR_INDCT_NM ,INDCT_SEQ ,USER_YN ,SYS_FRST_INPT_DT ,SYS_FRST_INPT_USER_ID ,SYS_FRST_INPT_PRGRM_ID ,SYS_LAST_CHG_DT ,SYS_LAST_CHG_USER_ID ,SYS_LAST_CHG_PRGRM_ID ,RMRK ) VALUES ( source.APC_CD ,source.SHPGOT_CRTR_TYPE ,source.CRTR_CD ,source.CRTR_VL ,source.CRTR_INDCT_NM ,source.INDCT_SEQ ,source.USER_YN ,SYSDATE ,source.SYS_FRST_INPT_USER_ID ,source.SYS_FRST_INPT_PRGRM_ID ,SYSDATE ,source.SYS_LAST_CHG_USER_ID ,source.SYS_LAST_CHG_PRGRM_ID ,source.RMRK )

MERGE INTO TB_SHPGOT_APC_CRTR_DTL SACD USING (
SELECT #{item.apcCd} AS APC_CD , #{item.shpgotCrtrType} AS SHPGOT_CRTR_TYPE , #{item.crtrCd} AS CRTR_CD , #{item.dtlSn} AS DTL_SN , #{item.dtlCd} AS DTL_CD , #{item.dtlVl} AS DTL_VL , #{item.dtlIndctNm} AS DTL_INDCT_NM , #{item.indctSeq} AS INDCT_SEQ , #{item.useYn} AS USE_YN , #{item.rmrk} AS RMRK , #{item.sysLastChgUserId} AS SYS_LAST_CHG_USER_ID , #{item.sysLastChgPrgrmId} AS SYS_LAST_CHG_PRGRM_ID , #{item.sysFrstInptUserId} AS SYS_FRST_INPT_USER_ID , #{item.sysFrstInptPrgrmId} AS SYS_FRST_INPT_PRGRM_ID FROM DUAL
) source ON ( SACD.APC_CD = source.APC_CD AND SACD.SHPGOT_CRTR_TYPE = source.SHPGOT_CRTR_TYPE AND SACD.CRTR_CD = source.CRTR_CD AND SACD.DTL_SN = source.DTL_SN ) WHEN MATCHED THEN UPDATE SET DTL_CD = source.DTL_CD ,DTL_VL = source.DTL_VL ,DTL_INDCT_NM = source.DTL_INDCT_NM ,INDCT_SEQ = source.INDCT_SEQ ,USE_YN = source.USE_YN ,RMRK = source.RMRK ,SYS_LAST_CHG_DT = SYSDATE ,SYS_LAST_CHG_USER_ID = source.SYS_LAST_CHG_USER_ID ,SYS_LAST_CHG_PRGRM_ID = source.SYS_LAST_CHG_PRGRM_ID WHEN NOT MATCHED THEN INSERT ( APC_CD ,SHPGOT_CRTR_TYPE ,CRTR_CD ,DTL_SN ,DTL_CD ,DTL_VL ,DTL_INDCT_NM ,INDCT_SEQ ,USE_YN ,DEL_YN ,SYS_FRST_INPT_DT ,SYS_FRST_INPT_PRGRM_ID ,SYS_FRST_INPT_USER_ID ,SYS_LAST_CHG_DT ,SYS_LAST_CHG_USER_ID ,SYS_LAST_CHG_PRGRM_ID ,RMRK ) VALUES ( #{apcCd} ,#{shpgotCrtrType} ,#{crtrCd} ,#{dtlSn} ,#{dtlCd} ,#{dtlVl} ,#{dtlIndctNm} ,#{indctSeq} ,#{useYn} ,#{delYn} ,SYSDATE ,#{sysFrstInptPrgrmId} ,#{sysFrstInptUserId} ,SYSDATE ,#{sysLastChgUserId} ,#{sysLastChgPrgrmId} ,#{rmrk} )
