mapper.select:
SELECT pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_PRDCR_NM(pt0.PRDCR_CD, pt0.APC_CD) FROM DUAL) AS PRDCR_NM FROM ( SELECT /* com.at.apcss.am.spmt.mapper.SlsMngMapper.selectSlsPrfmnc */ SPD.APC_CD AS APC_CD ,SPD.PRDCR_CD AS PRDCR_CD ,SP.SLS_YMD AS SLS_YMD ,SP.ITEM_CD AS ITEM_CD ,SP.VRTY_CD AS VRTY_CD ,SP.BRND_NM AS BRND_NM ,SP.SPMT_YMD AS SPMT_YMD ,SP.QNTT AS QNTT ,SP.WGHT AS WGHT ,SP.SLS_UNTPRC AS SLS_UNTPRC ,SP.RKNG_AMT AS RKNG_AMT ,SP.CFMTN_AMT AS CFMTN_AMT ,SP.SPMTNO AS SPMTNO ,SP.SLS_SN AS SLS_SN FROM TB_SPMT_PRFMNC_DTL SPD LEFT JOIN TB_SLS_PRFMNC SP ON SPD.APC_CD = SP.APC_CD AND SPD.SPMTNO = SP.SPMTNO WHERE SP.DEL_YN = 'N'
AND SPD.APC_CD = #{apcCd}
AND SP.SLS_YMD >= #{slsYmdFrom}
AND SP.SLS_YMD <= #{slsYmdTo}
AND SP.ITEM_CD = #{itemCd}
) pt0

mapper.update:

    	UPDATE /* com.at.apcss.am.spmt.mapper.SlsMngMapper.deleteSlsPrfmnc */
    	  TB_SLS_PRFMNC SET
			DEL_YN = 'Y'
			, SYS_LAST_CHG_DT = SYSDATE
			, SYS_LAST_CHG_USER_ID = #{sysLastChgUserId}
			, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}

		 WHERE APC_CD = #{apcCd}
		   AND SLS_YMD >= #{slsYmdFrom}
		   AND SLS_YMD <= #{slsYmdTo}
		   AND SLS_SN = #{slsSn}


    	UPDATE /* com.at.apcss.am.spmt.mapper.SlsMngMapper.updateSlsPrfmnc */
    	  TB_SLS_PRFMNC SET
			 ITEM_CD = #{itemCd}
			, VRTY_CD = #{vrtyCd}
			, QNTT = #{qntt}
			, WGHT = #{wght}
			, RKNG_AMT = #{rkngAmt}
			, CFMTN_AMT = #{cfmtnAmt}
			, SYS_LAST_CHG_DT = SYSDATE
			, SYS_LAST_CHG_USER_ID = #{sysLastChgUserId}
			, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}

		 WHERE APC_CD = #{apcCd}
		   AND SLS_YMD >= #{slsYmdFrom}
		   AND SLS_YMD <= #{slsYmdTo}
		   AND SLS_SN = #{slsSn}

mapper.select:
SELECT pt0.* , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM FROM ( SELECT /* com.at.apcss.am.spmt.mapper.SlsMngMapper.selectSlsUntprc */ SPU.APC_CD AS APC_CD, SPU.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD, SPU.SPMT_PCKG_UNIT_NM AS SPMT_PCKG_UNIT_NM, SPU.ITEM_CD AS ITEM_CD, SPU.VRTY_CD AS VRTY_CD, SPU.SPCFCT_CD AS SPCFCT_CD, SPU.NTSL_UNTPRC AS NTSL_UNTPRC, SPU.DEL_YN AS DEL_YN, SPU.GDS_GRD AS GDS_GRD, SPU.BRND_NM AS BRND_NM, SSUR.APLCN_CRTR_YMD AS APLCN_CRTR_YMD, SSUR.SPMT_SLS_UNTPRC AS SPMT_SLS_UNTPRC, SSUR.RMRK AS RMRK FROM TB_SPMT_PCKG_UNIT SPU LEFT JOIN TB_SPMT_SLS_UNTPRC_REG SSUR ON SSUR.SPMT_PCKG_UNIT_CD = SPU.SPMT_PCKG_UNIT_CD AND SSUR.APC_CD = SPU.APC_CD WHERE SPU.DEL_YN = 'N' AND SSUR.DEL_YN = 'N'
AND SPU.APC_CD = #{apcCd}
AND SSUR.APLCN_CRTR_YMD = #{aplcnCrtrYmd}
AND SPU.ITEM_CD = #{itemCd}
AND SPU.VRTY_CD = #{vrtyCd}
AND SPU.SPMT_PCKG_UNIT_NM LIKE '%'||#{gdsNm}||'%'
) pt0

mapper.update:

	    	UPDATE  /*com.at.apcss.am.spmt.mapper.SlsMngMapper.deleteSlsUntprc*/
				TB_SPMT_SLS_UNTPRC_REG	    	
	    	SET DEL_YN = 'Y'
	    		, SYS_LAST_CHG_DT = SYSDATE
				, SYS_LAST_CHG_USER_ID = #{sysLastChgUserId}
				, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}	    		
	    	WHERE APC_CD = #{apcCd}
    		AND SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
    		AND SPMT_SLS_UNTPRC_CD = #{spmtSlsUntprcCd}

mapper.select:
SELECT /* com.at.apcss.am.spmt.mapper.SlsMngMapper.selectSlsUntprcDtl */ SSUR.APC_CD AS APC_CD ,SSUR.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD ,SSUR.SPMT_SLS_UNTPRC_CD AS SPMT_SLS_UNTPRC_CD ,SSUR.SPMT_SLS_UNTPRC AS SPMT_SLS_UNTPRC ,SSUR.RMRK AS RMRK ,SSUR.DEL_YN AS DEL_YN ,SSUR.APLCN_CRTR_YMD AS APLCN_CRTR_YMD FROM TB_SPMT_SLS_UNTPRC_REG SSUR LEFT JOIN TB_SPMT_PCKG_UNIT SPU ON SSUR.SPMT_PCKG_UNIT_CD = SPU.SPMT_PCKG_UNIT_CD AND SSUR.APC_CD = SPU.APC_CD WHERE SPU.DEL_YN = 'N' AND SSUR.DEL_YN = 'N'
AND SSUR.APC_CD = #{apcCd}
AND SPU.ITEM_CD = #{itemCd}
AND SPU.VRTY_CD = #{vrtyCd}
AND SPU.SPCFCT_CD = #{spcfctCd}

mapper.update:

UPDATE
	TB_SPMT_SLS_UNTPRC_REG
SET
    SPMT_SLS_UNTPRC = #{spmtSlsUntprc}
	,RMRK = #{rmrk}
	,APLCN_CRTR_YMD = #{aplcnCrtrYmd}
  	,SYS_LAST_CHG_DT = SYSDATE
	,SYS_LAST_CHG_USER_ID = #{spmtPckgUnitCd}
	,SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}
WHERE
    APC_CD = #{apcCd}
	AND SPMT_PCKG_UNIT_CD = #{spmtPckgUnitCd}
	AND SPMT_SLS_UNTPRC_CD = #{spmtSlsUntprcCd}

mapper.insert:

INSERT INTO
    TB_SPMT_SLS_UNTPRC_REG
	(
		APC_CD
		,SPMT_PCKG_UNIT_CD
		,SPMT_SLS_UNTPRC_CD
		,SPMT_SLS_UNTPRC
		,RMRK
		,SYS_FRST_INPT_DT
		,SYS_FRST_INPT_USER_ID
		,SYS_FRST_INPT_PRGRM_ID
		,SYS_LAST_CHG_DT
		,SYS_LAST_CHG_USER_ID
		,SYS_LAST_CHG_PRGRM_ID
		,APLCN_CRTR_YMD
	)
	VALUES (
	        #{apcCd}
	        ,#{spmtPckgUnitCd}
	        ,(SELECT FN_GET_ID_SPMT_SLS_UNTPRC_CD(#{apcCd}) FROM DUAL)
	        ,#{spmtSlsUntprc}
	        ,#{rmrk}
	        ,SYSDATE
	        ,#{sysFrstInptUserId}
	        ,#{sysFrstInptPrgrmId}
	        ,SYSDATE
	        ,#{sysLastChgUserId}
	        ,#{sysLastChgPrgrmId}
	        ,#{aplcnCrtrYmd}
		   )
