mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* ,(SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM ,(SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM ,(SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT pt0.* ,(SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM ,(SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM ,(SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT OI.APC_CD AS APC_CD ,OI.APC_SE_CD AS APC_SE_CD ,OI.OUTORDRNO AS OUTORDRNO ,OI.RCPTN_SE_CD AS RCPTN_SE_CD ,OI.RCPTNNO AS RCPTNNO ,OI.RCPT_CFMTN_APC_CD AS RCPT_CFMTN_APC_CD ,OI.RCPT_CFMTN_YMD AS RCPT_CFMTN_YMD ,OI.PCKG_CMNDNO AS PCKG_CMNDNO ,OI.DOCNO AS DOCNO ,OI.CNPT_ORDRNO AS CNPT_ORDRNO ,OI.OUTORDR_TYPE AS OUTORDR_TYPE ,OI.WRHS_TYPE AS WRHS_TYPE ,OI.OUTORDR_YMD AS OUTORDR_YMD ,OI.OUTORDR_PRSN AS OUTORDR_PRSN ,OI.OUTORDR_PRSN_CD AS OUTORDR_PRSN_CD ,OI.DOCK_INFO AS DOCK_INFO ,OI.SPLY_PRSN AS SPLY_PRSN ,OI.GDS_CD AS GDS_CD ,OI.ITEM_CD AS ITEM_CD ,OI.VRTY_CD AS VRTY_CD ,OI.SPCFCT_CD AS SPCFCT_CD ,OI.TPND AS TPND ,OI.TPNB AS TPNB ,OI.DIAMONDLINE AS DIAMONDLINE ,OI.DLDTN AS DLDTN ,OI.BX_GDS_QNTT AS BX_GDS_QNTT ,OI.OUTORDR_QNTT AS OUTORDR_QNTT ,OI.PIECE_QNTT AS PIECE_QNTT ,OI.UNIT AS UNIT ,OI.BX_UNTPRC AS BX_UNTPRC ,OI.OUTORDR_UNTPRC AS OUTORDR_UNTPRC ,OI.OUTORDR_UNIT AS OUTORDR_UNIT ,OI.LOT AS LOT ,OI.TX_AMT AS TX_AMT ,OI.OUTORDR_AMT AS OUTORDR_AMT ,OI.DLDTN_CD AS DLDTN_CD ,OI.WRHS_YMD AS WRHS_YMD ,OI.DLVGDS_NM AS DLVGDS_NM ,OI.DLVGDS_CD AS DLVGDS_CD ,OI.APC_CNPT_NM AS APC_CNPT_NM ,OI.APC_CNPT_CD AS APC_CNPT_CD ,OI.CNTR_SE AS CNTR_SE ,OI.CNTR_RCVD_WRHS_YMD AS CNTR_RCVD_WRHS_YMD ,OI.CNTR_CD AS CNTR_CD ,OI.CNTR_NM AS CNTR_NM ,OI.DLVGDS_SE_CD AS DLVGDS_SE_CD ,OI.ETC AS ETC ,OI.DEL_YN AS DEL_YN ,OI.SYS_FRST_INPT_DT AS SYS_FRST_INPT_DT ,OI.SYS_FRST_INPT_USER_ID AS SYS_FRST_INPT_USER_ID ,OI.SYS_FRST_INPT_PRGRM_ID AS SYS_FRST_INPT_PRGRM_ID ,OI.SYS_LAST_CHG_DT AS SYS_LAST_CHG_DT ,OI.SYS_LAST_CHG_USER_ID AS SYS_LAST_CHG_USER_ID ,OI.SYS_LAST_CHG_PRGRM_ID AS SYS_LAST_CHG_PRGRM_ID ,OI.BUYER_NM AS BUYER_NM ,GI.SPMT_QNTT AS SPMT_QNTT ,GI.INVNTR_QNTT AS INVNTR_QNTT FROM TB_OUTORDR_INFO OI LEFT OUTER JOIN (SELECT APC_CD, ITEM_CD, VRTY_CD, SPCFCT_CD, SUM(SPMT_QNTT) AS SPMT_QNTT, SUM(INVNTR_QNTT) AS INVNTR_QNTT FROM TB_GDS_INVNTR GROUP BY APC_CD, ITEM_CD, VRTY_CD, SPCFCT_CD) GI ON GI.APC_CD = OI.APC_CD AND GI.ITEM_CD = OI.ITEM_CD AND GI.VRTY_CD = OI.VRTY_CD AND GI.SPCFCT_CD = OI.SPCFCT_CD
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* , (SELECT FN_GET_COM_CD_VL_NM('PCKG_SE_CD', pt0.PCKG_SE_CD, pt0.APC_CD) FROM DUAL) AS PCKG_SE , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT pt0.* , (SELECT FN_GET_COM_CD_VL_NM('PCKG_SE_CD', pt0.PCKG_SE_CD, pt0.APC_CD) FROM DUAL) AS PCKG_SE , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT /* com.at.apcss.am.spmt.mapper.SpmtCmndMapper.selectSpmtCmndList */ SC.APC_CD AS APC_CD , SC.CMND_YMD AS CMND_YMD , SC.PCKG_SE_CD AS PCKG_SE_CD , SC.ITEM_CD AS ITEM_CD , SC.VRTY_CD AS VRTY_CD , SC.SPCFCT_CD AS SPCFCT_CD , SC.GDS_CD AS GDS_CD , (SELECT CI.CNPT_NM FROM TB_CNPT_INFO CI WHERE CI.APC_CD = SC.APC_CD AND CI.CNPT_CD = SC.CNPT_CD AND CI.DEL_YN = 'N' AND SC.DEL_YN = 'N') AS CNPT_NM , SC.DLDTN AS DLDTN , SC.TRSPRT_CO_NM AS TRSPRT_CO_NM , SC.GDS_GRD AS GDS_GRD , SC.CMND_QNTT AS CMND_QNTT , SC.CMND_WGHT AS CMND_WGHT , SC.RMRK AS RMRK FROM TB_SPMT_CMND SC WHERE SC.APC_CD = #{apcCd} AND SC.DEL_YN = 'N'
AND SC.CMND_YMD >= #{cmndYmdFrom}
AND #{cmndYmdTo} >= SC.CMND_YMD
AND SC.CNPT_CD = #{cnptCd}
AND SC.TRSPRT_CO_CD = #{trsprtCoCd}
AND SC.ITEM_CD = #{itemCd}
AND SC.VRTY_CD = #{vrtyCd}
AND SC.SPCFCT_CD = #{spcfctCd}
AND SC.PCKG_SE_CD = #{pckgSeCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

mapper.insert:

    	INSERT INTO TB_SPMT_CMND
    		(
    			APC_CD
