		      , TRSPRT_CO_NM
    		  , GDS_GRD
    		  , CMND_QNTT
    		  , CMND_WGHT
    		  , PCKG_SE_CD
    		  , ORDR_NO
    		  , ITEM_CD
    		  , VRTY_CD
    		  , SPCFCT_CD
    		  , GDS_CD
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{spmtCmndNo}
    		  , #{spmtCmndSn}
    		  , #{cmndYmd}
    		  , #{cnptCd}
    		  , #{dldtn}
    		  , #{trsprtCoCd}
    		  , #{trsprtCoNm}
    		  , #{gdsGrd}
    		  , #{cmndQntt}
    		  , #{cmndWght}
    		  , #{pckgSeCd}
    		  , #{ordrNo}
    		  , #{itemCd}
    		  , #{vrtyCd}
    		  , #{spcfctCd}
    		  , #{gdsCd}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_SPMT_CMND
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , TRSPRT_CO_NM				= #{trsprtCoNm}
    		 , CMND_YMD 				= #{cmndYmd}
    		 , CNPT_CD 					= #{cnptCd}
    		 , DLDTN 					= #{dldtn}
    		 , TRSPRT_CO_CD 			= #{trsprtCoCd}
		     , TRSPRT_CO_NM 			= #{trsprtCoNm}
    		 , GDS_GRD 					= #{gdsGrd}
    		 , CMND_QNTT 				= #{cmndQntt}
    		 , CMND_WGHT 				= #{cmndWght}
    		 , PCKG_SE_CD 				= #{pckgSeCd}
    		 , ORDR_NO 					= #{ordrNo}
    		 , ITEM_CD 					= #{itemCd}
    		 , VRTY_CD 					= #{vrtyCd}
    		 , SPCFCT_CD 				= #{spcfctCd}
    		 , GDS_CD 					= #{gdsCd}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND SPMT_CMNDNO 				= #{spmtCmndNo}
		   AND SPMT_CMND_SN 			= #{spmtCmndSn}

mapper.delete:

DELETE FROM TB_SPMT_CMND
 WHERE APC_CD 					= #{apcCd}
   AND SPMT_CMNDNO 				= #{spmtCmndNo}
   AND SPMT_CMND_SN 			= #{spmtCmndSn}
