mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* , (SELECT FN_GET_COM_CD_VL_NM('PCKG_SE_CD', pt0.PCKG_SE_CD, pt0.APC_CD) FROM DUAL) AS PCKG_SE , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT pt0.* , (SELECT FN_GET_COM_CD_VL_NM('PCKG_SE_CD', pt0.PCKG_SE_CD, pt0.APC_CD) FROM DUAL) AS PCKG_SE , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM FROM (
SELECT /* com.at.apcss.am.spmt.mapper.SpmtCmndMapper.selectSpmtCmndList */ SC.APC_CD AS APC_CD , SC.CMND_YMD AS CMND_YMD , SC.PCKG_SE_CD AS PCKG_SE_CD , SC.ITEM_CD AS ITEM_CD , SC.VRTY_CD AS VRTY_CD , SC.SPCFCT_CD AS SPCFCT_CD , SC.GDS_CD AS GDS_CD , (SELECT CI.CNPT_NM FROM TB_CNPT_INFO CI WHERE CI.APC_CD = SC.APC_CD AND CI.CNPT_CD = SC.CNPT_CD AND CI.DEL_YN = 'N' AND SC.DEL_YN = 'N') AS CNPT_NM , SC.DLDTN AS DLDTN , SC.TRSPRT_CO_NM AS TRSPRT_CO_NM , SC.GDS_GRD AS GDS_GRD , SC.CMND_QNTT AS CMND_QNTT , SC.CMND_WGHT AS CMND_WGHT , SC.RMRK AS RMRK FROM TB_SPMT_CMND SC WHERE SC.APC_CD = #{apcCd} AND SC.DEL_YN = 'N'
AND SC.CMND_YMD >= #{cmndYmdFrom}
AND #{cmndYmdTo} >= SC.CMND_YMD
AND SC.CNPT_CD = #{cnptCd}
AND SC.TRSPRT_CO_CD = #{trsprtCoCd}
AND SC.ITEM_CD = #{itemCd}
AND SC.VRTY_CD = #{vrtyCd}
AND SC.SPCFCT_CD = #{spcfctCd}
AND SC.PCKG_SE_CD = #{pckgSeCd}
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

mapper.insert:

    	INSERT INTO TB_SPMT_CMND
    		(
    			APC_CD
    		  , SPMT_CMNDNO
    		  , SPMT_CMND_SN
    		  , CMND_YMD
    		  , CNPT_CD
    		  , DLDTN
    		  , TRSPRT_CO_CD
		      , TRSPRT_CO_NM
    		  , GDS_GRD
    		  , CMND_QNTT
    		  , CMND_WGHT
    		  , PCKG_SE_CD
    		  , ORDR_NO
    		  , ITEM_CD
    		  , VRTY_CD
    		  , SPCFCT_CD
    		  , GDS_CD
    		  , RMRK
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , #{spmtCmndNo}
    		  , #{spmtCmndSn}
    		  , #{cmndYmd}
    		  , #{cnptCd}
    		  , #{dldtn}
    		  , #{trsprtCoCd}
    		  , #{trsprtCoNm}
    		  , #{gdsGrd}
    		  , #{cmndQntt}
    		  , #{cmndWght}
    		  , #{pckgSeCd}
    		  , #{ordrNo}
    		  , #{itemCd}
    		  , #{vrtyCd}
    		  , #{spcfctCd}
    		  , #{gdsCd}
    		  , #{rmrk}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_SPMT_CMND
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , TRSPRT_CO_NM				= #{trsprtCoNm}
    		 , CMND_YMD 				= #{cmndYmd}
    		 , CNPT_CD 					= #{cnptCd}
    		 , DLDTN 					= #{dldtn}
    		 , TRSPRT_CO_CD 			= #{trsprtCoCd}
		     , TRSPRT_CO_NM 			= #{trsprtCoNm}
    		 , GDS_GRD 					= #{gdsGrd}
    		 , CMND_QNTT 				= #{cmndQntt}
    		 , CMND_WGHT 				= #{cmndWght}
    		 , PCKG_SE_CD 				= #{pckgSeCd}
    		 , ORDR_NO 					= #{ordrNo}
    		 , ITEM_CD 					= #{itemCd}
    		 , VRTY_CD 					= #{vrtyCd}
    		 , SPCFCT_CD 				= #{spcfctCd}
    		 , GDS_CD 					= #{gdsCd}
    		 , RMRK						= #{rmrk}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND SPMT_CMNDNO 				= #{spmtCmndNo}
		   AND SPMT_CMND_SN 			= #{spmtCmndSn}

mapper.delete:

DELETE FROM TB_SPMT_CMND
 WHERE APC_CD 					= #{apcCd}
   AND SPMT_CMNDNO 				= #{spmtCmndNo}
   AND SPMT_CMND_SN 			= #{spmtCmndSn}
