mapper.select:
SELECT ROWNUM AS ROW_SEQ , COUNT(*) OVER() AS TOTAL_RECORD_COUNT , pt0.* , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM , (SELECT FN_GET_TRSPRT_CO_NM(pt0.TRSPRT_CO_CD, pt0.APC_CD) FROM DUAL) AS TRSPRT_CO_NM , SUM(pt0.SPMT_QNTT) OVER() AS TOT_SPMT_QNTT , SUM(pt0.SPMT_WGHT) OVER() AS TOT_SPMT_WGHT FROM (
SELECT pt0.* , (SELECT FN_GET_GDS_NM(pt0.GDS_CD, pt0.APC_CD) FROM DUAL) AS GDS_NM , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM , (SELECT FN_GET_TRSPRT_CO_NM(pt0.TRSPRT_CO_CD, pt0.APC_CD) FROM DUAL) AS TRSPRT_CO_NM , SUM(pt0.SPMT_QNTT) OVER() AS TOT_SPMT_QNTT , SUM(pt0.SPMT_WGHT) OVER() AS TOT_SPMT_WGHT FROM (
SELECT /* com.at.apcss.am.spmt.mapper.SpmtPrfmncMapper.selectSpmtPrfmncList */ PD.SPMTNO AS SPMTNO , PD.PCKG_SN AS PCKG_SN , PD.PCKGNO AS PCKGNO , PC.APC_CD AS APC_CD , PC.SPMT_YMD AS SPMT_YMD , PD.BRND_NM AS BRND_NM , PD.GDS_CD AS GDS_CD , PD.ITEM_CD AS ITEM_CD , PD.VRTY_CD AS VRTY_CD , PD.SPCFCT_CD AS SPCFCT_CD , PC.CNPT_CD AS CNPT_CD , MAX(PC.DLDTN) AS DLDTN , MAX(PC.TRSPRT_CO_CD) AS TRSPRT_CO_CD , MAX(PC.VHCLNO) AS VHCLNO , MAX(PC.TRSPRT_CST) AS TRSPRT_CST , SUM(PD.SPMT_QNTT) AS SPMT_QNTT , SUM(PD.SPMT_WGHT) AS SPMT_WGHT , MAX(PC.RMRK) AS RMRK , (SELECT DDLN_YN FROM TB_DDLN_MNG X WHERE X.APC_CD = PC.APC_CD AND SUBSTR(PC.SPMT_YMD, 1,4) = X.CRTR_YR AND SUBSTR(PC.SPMT_YMD, 5,2) = X.CRTR_MM ) AS DDLN_YN , (SELECT CFMTN_YN FROM TB_SLS_PRFMNC X WHERE X.APC_CD = PC.APC_CD AND X.SPMTNO = PD.SPMTNO AND X.PCKGNO = PD.PCKGNO AND X.PCKG_SN = PD.PCKG_SN ) AS CFMTN_YN FROM TB_SPMT_PRFMNC_COM PC , TB_SPMT_PRFMNC_DTL PD WHERE PC.APC_CD = PD.APC_CD AND PC.SPMTNO = PD.SPMTNO AND PC.DEL_YN = 'N' AND PD.DEL_YN = 'N'
AND PC.APC_CD = #{apcCd}
AND PC.SPMT_YMD >= #{spmtYmdFrom}
AND PC.SPMT_YMD <= #{spmtYmdTo}
AND (SELECT FN_GET_CNPT_NM(PC.CNPT_CD, PC.APC_CD) FROM DUAL) LIKE '%' || #{cnptNm} || '%'
AND PC.CNPT_CD = #{cnptCd}
AND PC.TRSPRT_CO_CD = #{trsprtCoCd}
AND PD.ITEM_CD = #{itemCd}
AND PD.VRTY_CD in (SELECT COL1 FROM TABLE(FN_SYS_SPLIT_STRING(#{vrtyCd})))
-- AND GI.WAREHOUSE_SE_CD = #{warehouseSeCd}
AND PC.DLDTN LIKE '%' || #{dldtn} || '%'
AND PC.VHCLNO LIKE '%' || #{vhclno} || '%'
GROUP BY PD.SPMTNO , PC.SPMT_YMD , PD.PCKGNO , PD.PCKG_SN , PD.BRND_NM , PD.GDS_CD , PD.ITEM_CD , PD.VRTY_CD , PD.SPCFCT_CD , PC.APC_CD , PC.CNPT_CD
) pt0 OFFSET (#{currentPageNo} - 1) * #{recordCountPerPage} ROWS FETCH FIRST #{recordCountPerPage} ROWS ONLY
) pt0

