mapper.update:

	UPDATE TB_SPMT_PRFMNC_COM
	   SET SYS_LAST_CHG_DT			= SYSDATE
    	 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    	 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
	   	 , DEL_YN 					= 'Y'
	 WHERE APC_CD 	= #{apcCd}
	   AND SPMTNO	= #{spmtno}


	UPDATE TB_SPMT_PRFMNC_DTL
	   SET SYS_LAST_CHG_DT			= SYSDATE
    	 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    	 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
	   	 , DEL_YN 					= 'Y'
	 WHERE APC_CD 	= #{apcCd}
	   AND SPMTNO	= #{spmtno}
	   AND PCKGNO   = #{pckgno}

mapper.select:
SELECT pt0.* /* com.at.apcss.am.spmt.mapper.SpmtPrfmncMapper.selectSpmtPrfmnDtlcList */ , (SELECT FN_GET_ITEM_NM(pt0.ITEM_CD, pt0.APC_CD) FROM DUAL) AS ITEM_NM , (SELECT FN_GET_VRTY_NM(pt0.ITEM_CD, pt0.VRTY_CD, pt0.APC_CD) FROM DUAL) AS VRTY_NM , (SELECT FN_GET_SPCFCT_NM(pt0.ITEM_CD, pt0.SPCFCT_CD, pt0.APC_CD) FROM DUAL) AS SPCFCT_NM , (SELECT FN_GET_COM_CD_VL_NM('GDS_GRD', pt0.GDS_GRD) FROM DUAL) AS GDS_GRD_NM , (SELECT FN_GET_SPMT_PCKG_UNIT_NM(pt0.SPMT_PCKG_UNIT_CD, pt0.APC_CD) FROM DUAL) AS SPMT_PCKG_UNIT_NM , (SELECT FN_GET_CNPT_NM(pt0.CNPT_CD, pt0.APC_CD) FROM DUAL) AS CNPT_NM , (SELECT FN_GET_TRSPRT_CO_NM(pt0.TRSPRT_CO_CD, pt0.APC_CD) FROM DUAL) AS TRSPRT_CO_NM FROM (SELECT PD.APC_CD AS APC_CD , PD.SPMTNO AS SPMTNO , PC.SPMT_YMD AS SPMT_YMD , PD.ITEM_CD AS ITEM_CD , PD.VRTY_CD AS VRTY_CD , PD.SPCFCT_CD AS SPCFCT_CD , PD.GDS_GRD AS GDS_GRD , PD.SPMT_PCKG_UNIT_CD AS SPMT_PCKG_UNIT_CD , MAX(PC.CNPT_CD) AS CNPT_CD , MAX(PC.TRSPRT_CO_CD) AS TRSPRT_CO_CD , MAX(PC.RMRK) AS RMRK , MAX(PC.DEL_YN) AS DEL_YN , MAX(PC.VHCLNO) AS VHCLNO , MAX(PC.DLDTN) AS DLDTN , MAX(PC.TRSPRT_CST) AS TRSPRT_CST , MAX(PC.SPMT_SE_CD) AS SPMT_SE_CD , MAX(PC.SPMT_PRSN_DCLRNO) AS SPMT_PRSN_DCLRNO , MAX(PC.PLOR_CD) AS PLOR_CD , MAX(PD.PCKGNO) AS PCKGNO , MAX(PD.PCKG_SN) AS PCKG_SN , MAX(PD.BRND_NM) AS BRND_NM , MAX(PD.GDS_CD) AS GDS_CD , MAX(PD.PRDCR_CD) AS PRDCR_CD , MAX(PD.SPMT_CMNDNO) AS SPMT_CMNDNO , SUM(PD.SPMT_QNTT) AS SPMT_QNTT , SUM(PD.SPMT_WGHT) AS SPMT_WGHT FROM TB_SPMT_PRFMNC_COM PC , TB_SPMT_PRFMNC_DTL PD WHERE PC.APC_CD = PD.APC_CD AND PC.SPMTNO = PD.SPMTNO AND PC.DEL_YN = 'N' AND PD.DEL_YN = 'N'
AND PD.APC_CD = #{apcCd}
AND PC.SPMT_YMD = #{spmtYmd}
AND PD.VRTY_CD = #{vrtyCd}
GROUP BY PD.APC_CD, PD.SPMTNO, PC.SPMT_YMD , PD.ITEM_CD, PD.VRTY_CD, PD.SPCFCT_CD, PD.GDS_GRD, PD.SPMT_PCKG_UNIT_CD ) pt0
