mapper.insert:

    	INSERT INTO TB_SPMT_TRSPRT_CO_INFO
    		(
    			APC_CD
    		  , TRSPRT_CO_CD
		      , TRSPRT_CO_NM
		      , TELNO
		      , FXNO
    		  , RMRK
    		  , EXTRNL_LNKG_CD
    		  , DEL_YN
    		  , SYS_FRST_INPT_DT
    		  , SYS_FRST_INPT_USER_ID
    		  , SYS_FRST_INPT_PRGRM_ID
    		  , SYS_LAST_CHG_DT
    		  , SYS_LAST_CHG_USER_ID
    		  , SYS_LAST_CHG_PRGRM_ID
    		)
   		VALUES
   			(
   				#{apcCd}
    		  , FN_GET_ID_SPMT_TRSPRT_CO(#{apcCd})
    		  , #{trsprtCoNm}
    		  , #{telno}
    		  , #{fxno}
    		  , #{rmrk}
    		  , #{extrnlLnkgCd}
    		  , #{delYn}
    		  , SYSDATE
    		  , #{sysFrstInptUserId}
    		  , #{sysFrstInptPrgrmId}
    		  , SYSDATE
    		  , #{sysLastChgUserId}
    		  , #{sysLastChgPrgrmId}
   			)

mapper.update:

		UPDATE TB_SPMT_TRSPRT_CO_INFO
		   SET SYS_LAST_CHG_DT 			= SYSDATE
    		 , SYS_LAST_CHG_USER_ID		= #{sysLastChgUserId}
    		 , SYS_LAST_CHG_PRGRM_ID 	= #{sysLastChgPrgrmId}
			 , TRSPRT_CO_NM				= #{trsprtCoNm}
    		 , TELNO					= #{telno}
    		 , FXNO						= #{fxno}
    		 , RMRK						= #{rmrk}
    		 , EXTRNL_LNKG_CD			= #{extrnlLnkgCd}
    		 , DEL_YN					= #{delYn}
		 WHERE APC_CD 					= #{apcCd}
		   AND TRSPRT_CO_CD 			= #{trsprtCoCd}

mapper.delete:

DELETE FROM TB_SPMT_TRSPRT_CO_INFO
 WHERE APC_CD			= #{apcCd}
   AND TRSPRT_CO_CD 	= #{trsprtCoCd}

mapper.select:

    	SELECT X.* /* com.at.apcss.am.spmt.mapper.SpmtTrsprtMapper.spmtTrsprtDelible */
		  FROM (
		           SELECT
		                  '출하지시' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SPMT_CMND X
		                     WHERE X.APC_CD 		= #{apcCd}
		                       AND X.TRSPRT_CO_CD 	= #{trsprtCoCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		           UNION ALL
		           SELECT
		                  '출하실적' AS DELIBLE
		                 , (SELECT COUNT(*)
		                      FROM TB_SPMT_PRFMNC_COM X
		                     WHERE X.APC_CD 		= #{apcCd}
		                       AND X.TRSPRT_CO_CD 	= #{trsprtCoCd}
		                       AND X.DEL_YN = 'N'
		                  )  AS CNT
		             FROM DUAL
		         ) X
		 WHERE CNT > 0
