mapper.select:
SELECT /* com.at.apcss.am.tot.mapper.TotMngMapper.selectCrtrList */
C.APC_CD AS APC_CD , C.TOT_CRTR_TYPE AS TOT_CRTR_TYPE , CT.CD_VL_NM AS TOT_CRTR_TYPE_NM , CT.INDCT_SEQ AS CRTR_TYPE_SEQ , C.CRTR_CD AS CRTR_CD , C.CRTR_VL AS CRTR_VL , C.TOT_DTL_TYPE AS TOT_DTL_TYPE , C.CRTR_INDCT_NM AS CRTR_INDCT_NM , C.INDCT_SEQ AS INDCT_SEQ , C.INDCT_SEQ AS CRTR_SEQ , C.USE_YN AS USE_YN , CC.CD_CHR_VL AS TOT_CRTR_CHR_VL
FROM TB_TOT_CRTR C
JOIN TB_COM_CD_DTL CT ON CT.CD_ID = 'TOT_CRTR_TYPE' AND CT.APC_CD = '0000' AND CT.CD_VL = C.TOT_CRTR_TYPE AND CT.DEL_YN = 'N'
JOIN TB_COM_CD_DTL CC ON CC.CD_ID = 'TOT_CRTR_CD' AND CC.APC_CD = '0000' AND CC.CD_VL = C.CRTR_CD AND CC.DEL_YN = 'N'
WHERE C.DEL_YN = 'N'
AND C.APC_CD = #{apcCd}
AND C.TOT_CRTR_TYPE = #{totCrtrType}
AND C.CRTR_CD = #{crtrCd}
AND C.CRTR_INDCT_NM LIKE '%' || #{crtrIndctNm} || '%'
AND C.USE_YN = #{useYn}
ORDER BY CRTR_TYPE_SEQ, CRTR_SEQ

SELECT /* com.at.apcss.am.tot.mapper.TotMngMapper.selectCrtrDtl */ CD.APC_CD AS APC_CD , CD.TOT_CRTR_TYPE AS TOT_CRTR_TYPE , CD.CRTR_CD AS CRTR_CD , CD.DTL_SN AS DTL_SN , CD.DTL_CD AS DTL_CD , CD.DTL_VL AS DTL_VL , CD.DTL_INDCT_NM AS DTL_INDCT_NM , CD.TOT_DTL_PRCS_CD AS TOT_DTL_PRCS_CD , CD.USE_YN AS USE_YN , CD.DEL_YN AS DEL_YN
FROM TB_TOT_CRTR_DTL CD
WHERE CD.APC_CD = #{apcCd} AND CD.TOT_CRTR_TYPE = #{totCrtrType} AND CD.CRTR_CD = #{crtrCd} AND CD.DTL_SN = #{dtlSn}

SELECT /* com.at.apcss.am.tot.mapper.TotMngMapper.selectCrtrDtlList */ C.APC_CD AS APC_CD , C.TOT_CRTR_TYPE AS TOT_CRTR_TYPE , CT.CD_VL_NM AS TOT_CRTR_TYPE_NM , CT.INDCT_SEQ AS CRTR_TYPE_SEQ , C.CRTR_CD AS CRTR_CD , C.CRTR_VL AS CRTR_VL , C.CRTR_INDCT_NM AS CRTR_INDCT_NM , C.TOT_DTL_TYPE AS TOT_DTL_TYPE , C.INDCT_SEQ AS CRTR_SEQ , C.USE_YN AS CRTR_USE_YN , CC.CD_CHR_VL AS TOT_CRTR_CHR_VL , CD.DTL_SN AS DTL_SN , CD.DTL_CD AS DTL_CD , CD.DTL_VL AS DTL_VL , CD.DTL_INDCT_NM AS DTL_INDCT_NM , CD.TOT_DTL_PRCS_CD AS TOT_DTL_PRCS_CD , CD.USE_YN AS CRTR_DTL_USE_YN , CD.INDCT_SEQ AS INDCT_SEQ , CD.INDCT_SEQ AS CRTR_DTL_SEQ , CD.USE_YN AS USE_YN
FROM TB_TOT_CRTR C
JOIN TB_COM_CD_DTL CT ON CT.CD_ID = 'TOT_CRTR_TYPE' AND CT.APC_CD = '0000' AND CT.CD_VL = C.TOT_CRTR_TYPE AND CT.DEL_YN = 'N'
JOIN TB_COM_CD_DTL CC ON CC.CD_ID = 'TOT_CRTR_CD' AND CC.APC_CD = '0000' AND CC.CD_VL = C.CRTR_CD AND CC.DEL_YN = 'N'
JOIN TB_TOT_CRTR_DTL CD ON CD.APC_CD = C.APC_CD AND CD.TOT_CRTR_TYPE = C.TOT_CRTR_TYPE AND CD.CRTR_CD = C.CRTR_CD AND CD.DEL_YN = 'N'
WHERE C.DEL_YN = 'N'
AND C.APC_CD = #{apcCd}
AND C.TOT_CRTR_TYPE = #{totCrtrType}
AND C.CRTR_CD = #{crtrCd}
AND C.USE_YN = #{crtrUseYn}
AND CD.USE_YN = #{crtrDtlUseYn}
ORDER BY CRTR_TYPE_SEQ, CRTR_SEQ, CRTR_DTL_SEQ, DTL_SN

