    		SELECT
				* FROM TB_TOT_SPMT_PRFMNC A
				WHERE 1=1
					AND A.APC_CD = #{apcCd}


				AND A.TOT_YMD >= #{totYmdFrom}


				AND A.TOT_YMD >= #{totYmdTo}


	   			AND A.ITEM_CD = #{itemCd}


	   			AND A.VRTY_CD = #{vrtyCd}


    		SELECT
				* FROM TB_TOT_GDS_INVNTR A
				WHERE 1=1
					AND A.APC_CD = #{apcCd}


				AND A.TOT_YMD >= #{totYmdFrom}


				AND A.TOT_YMD >= #{totYmdTo}


	   			AND A.ITEM_CD = #{itemCd}


	   			AND A.VRTY_CD = #{vrtyCd}


    		SELECT
				* FROM TB_TOT_PCKG_PRFMNC A
				WHERE 1=1
					AND A.APC_CD = #{apcCd}


				AND A.TOT_YMD >= #{totYmdFrom}


				AND A.TOT_YMD >= #{totYmdTo}


	   			AND A.ITEM_CD = #{itemCd}


	   			AND A.VRTY_CD = #{vrtyCd}


    		SELECT
				* FROM TB_TOT_RAW_MTR_INVNTR A
				WHERE 1=1
					AND A.APC_CD = #{apcCd}


				AND A.TOT_YMD >= #{totYmdFrom}


				AND A.TOT_YMD >= #{totYmdTo}


	   			AND A.ITEM_CD = #{itemCd}


	   			AND A.VRTY_CD = #{vrtyCd}


    		SELECT

    		    A.WRHS_YMD
    		    , A.ITEM_CD
    		    , A.VRTY_CD
    		          , AL

				* FROM TB_TOT_RAW_MTR_WRHS A
				WHERE 1=1
					AND A.APC_CD = #{apcCd}




				AND A.TOT_YMD >= #{totYmdFrom}


