mapper.insert:

    	INSERT /* com.at.apcss.am.tot.mapper.insertTotMngInfo  */
    	  INTO TB_TOT_CRTR (
			APC_CD
			, TOT_CRTR_TYPE
			, CRTR_CD
			, CRTR_VL
			, CRTR_INDCT_NM
			, TOT_DTL_TYPE
			, INDCT_SEQ
			, USE_YN
			, DEL_YN
			, SYS_FRST_INPT_DT
			, SYS_FRST_INPT_USER_ID
			, SYS_FRST_INPT_PRGRM_ID
			, SYS_LAST_CHG_DT
			, SYS_LAST_CHG_USER_ID
			, SYS_LAST_CHG_PRGRM_ID



    	  ) VALUES (

			#{apcCd}
			, #{totCrtrType}
			, #{crtrCd}
			, #{crtrVl}
			, #{crtrIndctNm}
			, #{totDtlType}
			, #{indctSeq}
			, #{useYn}
			, #{delYn}
			, SYSDATE
			, #{sysFrstInptUserId}
			, #{sysFrstInptPrgrmId}
			, SYSDATE
			, #{sysLastChgUserId}
			, #{sysLastChgPrgrmId}
    	  )


    	INSERT /* com.at.apcss.am.tot.mapper.inserttotMngDtlInfo  */
    	  INTO TB_TOT_CRTR_DTL (
			APC_CD
			, TOT_CRTR_TYPE
			, CRTR_CD
			, DTL_SN
			, DTL_CD
			, DTL_VL
			, DTL_INDCT_NM
			, TOT_DTL_PRCS_CD
			, INDCT_SEQ
			, USE_YN
			, DEL_YN
			, SYS_FRST_INPT_DT
			, SYS_FRST_INPT_USER_ID
			, SYS_FRST_INPT_PRGRM_ID
			, SYS_LAST_CHG_DT
			, SYS_LAST_CHG_USER_ID
			, SYS_LAST_CHG_PRGRM_ID


    	  ) VALUES (
			#{apcCd}
			, #{totCrtrType}
			, #{crtrCd}
			, (SELECT COALESCE(MAX(DTL_SN), 0) + 1 FROM TB_TOT_CRTR_DTL)
			, #{dtlCd}
			, #{dtlVl}
			, #{dtlIndctNm}
			, #{totDtlPrcsCd}
			, #{indctSeq}
			, #{useYn}
			, #{delYn}
			, SYSDATE
			, #{sysFrstInptUserId}
			, #{sysFrstInptPrgrmId}
			, SYSDATE
			, #{sysLastChgUserId}
			, #{sysLastChgPrgrmId}
    	  )

mapper.update:

    	UPDATE /* com.at.apcss.am.apc.mapper.ApcEvrmntStngMapper.updatetotMngInfo */
    	  TB_TOT_CRTR SET
			CRTR_VL = #{crtrVl}
			, CRTR_INDCT_NM = #{crtrIndctNm}
			, TOT_DTL_TYPE = #{totDtlType}
			, INDCT_SEQ = #{indctSeq}
			, USE_YN = #{useYn}
			, SYS_LAST_CHG_DT = SYSDATE
			, SYS_LAST_CHG_USER_ID = #{sysLastChgUserId}
			, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}


		 WHERE APC_CD = #{apcCd}
		   AND TOT_CRTR_TYPE = #{totCrtrType}
		   AND CRTR_CD = #{crtrCd}


    	UPDATE /* com.at.apcss.am.apc.mapper.ApcEvrmntStngMapper.updatetotMngDtlInfo */
    	  TB_TOT_CRTR_DTL SET
			DTL_CD = #{dtlCd}
			, DTL_VL = #{dtlVl}
			, DTL_INDCT_NM = #{dtlIndctNm}
			, TOT_DTL_PRCS_CD = #{totDtlPrcsCd}
			, INDCT_SEQ = #{indctSeq}
			, USE_YN = #{useYn}
			, SYS_LAST_CHG_DT = SYSDATE
			, SYS_LAST_CHG_USER_ID = #{sysLastChgUserId}
			, SYS_LAST_CHG_PRGRM_ID = #{sysLastChgPrgrmId}



		 WHERE APC_CD = #{apcCd}
		   AND TOT_CRTR_TYPE = #{totCrtrType}
		   AND CRTR_CD = #{crtrCd}
		   AND DTL_SN = #{dtlSn}

mapper.delete:

DELETE /* com.at.apcss.am.tot.mapper.totMngMapper.deletetotMngInfo */
  FROM TB_TOT_CRTR
 WHERE APC_CD = #{apcCd}
   AND TOT_CRTR_TYPE = #{totCrtrType}
   AND CRTR_CD = #{crtrCd}


DELETE /* com.at.apcss.am.tot.mapper.totMngMapper.deletetotMngInfo */
  FROM TB_TOT_CRTR_DTL
 WHERE APC_CD = #{apcCd}
   AND TOT_CRTR_TYPE = #{totCrtrType}
   AND CRTR_CD = #{crtrCd}
   AND DTL_SN = #{dtlSn}
